importPackage(java.util);
importClass(java.text.SimpleDateFormat);
importClass(java.lang.System);


function invoke(msg)         {

	var timeNow = System.currentTimeMillis();
	var timeStart = msg.get("ws.backend.start.time");
	var totalTimeMs = timeNow - timeStart;

	var df= new SimpleDateFormat("dd-MM-yyyy HH:mm:ss", Locale.FRANCE);
	var dateEndDisplay = df.format(timeNow);

//	var dateEndDisplay = timeNow.toUTCString();
			
	var msgTrace = "TECHNIQUE#STAT_APPEL_BACK_END#" + dateEndDisplay + "#" + msg.get("http.request.verb")  + "#" + msg.get("destinationURL")  + "#" + msg.get("id")  + "#" + msg.get("request.id") + "#" + msg.get("authentication.subject.id") + "#" + msg.get("http.response.status") + "#" + totalTimeMs  + "#";

	msg.put("trace.technique.ws.backend", msgTrace);

	return true;
}

package icdc

import java.text.DateFormat
import java.text.SimpleDateFormat

import com.vordel.circuit.Message
import com.vordel.el.Selector

import groovy.transform.Field

@Field private static final DateFormat DATE_FORMAT =  new SimpleDateFormat("dd-MM-yyyy HH:mm:ss", Locale.FRANCE);
@Field private static final Selector<Long> START_TIME = new Selector<Long>(String.format("\$%s", "{ws.backend.start.time}"), Long.class);
@Field private static final Selector<String> REQUEST_VERB = new Selector<Long>(String.format("\$%s", "{http.request.verb}"), String.class);
@Field private static final Selector<String> REQUEST_URL = new Selector<Long>(String.format("\$%s", "{destinationURL}"), String.class);
@Field private static final Selector<String> MESSAGE_ID = new Selector<Long>(String.format("\$%s", "{id}"), String.class);
@Field private static final Selector<String> REQUEST_ID = new Selector<Long>(String.format("\$%s", "{request.id}"), String.class);
@Field private static final Selector<String> SUBJECT = new Selector<Long>(String.format("\$%s", "{authentication.subject.id}"), String.class);
@Field private static final Selector<Integer> HTTP_STATUS = new Selector<Long>(String.format("\$%s", "{http.response.status}"), Integer.class);

boolean invoke(Message msg) {
	long timeNow = System.currentTimeMillis();
	long timeStart = START_TIME.substitute(msg);
	long totalTimeMs = timeNow - timeStart;

	String msgTrace = String.format("TECHNIQUE#STAT_APPEL_BACK_END#%s#%s#%s#%s#%s#%s#%d#%d#",
			DATE_FORMAT.format(timeNow),
			REQUEST_VERB.substitute(msg),
			REQUEST_URL.substitute(msg),
			MESSAGE_ID.substitute(msg),
			REQUEST_ID.substitute(msg),
			SUBJECT.substitute(msg),
			HTTP_STATUS.substitute(msg),
			totalTimeMs);

	msg.put("trace.technique.ws.backend", msgTrace);

	return true;
}

