package icdc

import com.vordel.circuit.Message
import com.vordel.mime.Body
import com.vordel.trace.Trace

boolean invoke(Message msg) {
	Body body = msg.get("content.body");

	if (body != null) {
		long size = body.getContentLength(Body.WRITE_NO_CTE);

		msg.put("body.length", size);

		Trace.debug(String.format("body.length : %d", size));
	} else {
		msg.put("body.length", "n/a");
	}

	return true;
}

