importPackage(java.util);
importClass(java.text.SimpleDateFormat);
importClass(java.lang.System);

function invoke(msg)         {           
	
	var now = new Date(); 

	var df= new SimpleDateFormat("dd/MMMMM/yyyy:HH:mm:ss SSSS", Locale.FRANCE);
	var dateEndDisplay = df.format(now.getTime());

//	var dateEndDisplay = now.toUTCString();
	
	var action = msg.get("audit.action");
	var status = msg.get("audit.status");
	if(!action) {
		action = msg.get("message.uri");
		status = "1";
	}

	var provider = msg.get("audit.provider");
	var detail = msg.get("audit.detail");
	var userid = msg.get("authentication.subject.id");

	if (!userid) {
		userid = null;
	} else {
		userid = String(userid);
	}

	if (!detail) {
		detail = msg.get("msg.error.reason");
		if (!detail) {
			detail = "Aucun détail";
		}
	}	
	
	// AdresseIp 
	var ipSource = msg.get("request.ip.source");
	var ipOrigin = msg.get("request.ip.origin");

	if (!ipSource) {
		ipSource = msg.get("http.request.clientaddr");
		ipOrigin = msg.get("http.request.clientaddr");
	} 

	var trace = "AUDIT#SPS#"+dateEndDisplay  +"#" + ipSource +"#" +  ipOrigin +"#" + msg.get("id")  + "#" + msg.get("request.id") + "#" + action  +"#" + status +"#" + provider +"#" + userid +"#" + detail;
	msg.put("piste.audit", trace);

	return true;
}

importPackage(java.util);
importClass(java.text.SimpleDateFormat);
importClass(java.lang.System);


function invoke(msg)         {           
	
	var now = new Date(); 

	var df= new SimpleDateFormat("dd/MMMMM/yyyy:HH:mm:ss SSSS", Locale.FRANCE);
	var dateEndDisplay = df.format(now.getTime());
//	var dateEndDisplay = now.toUTCString();
	
	var startTime = msg.get("technique.stat.start");
	if (!startTime) startTime=msg.get("message.reception_time");

	var totalTimeMs = Math.round(parseInt(now.getTime()) - parseInt(startTime));
	msg.put("request.reponse.time", totalTimeMs);

	var userid = msg.get("authentication.subject.id");

	if (!userid) {
		userid = null;
	} else {
		userid = String(userid);
	}

	
	// Nom du path ou de l'API
	var apiName = msg.get("api.name");
	var targetURL;

	if (apiName) {
		var serviceName = msg.get("service.name");
		if(serviceName){
			targetURL = serviceName  + " - " + msg.get("api.method.name") + " - " + msg.get("api.method.path");
		}else{
			targetURL = msg.get("message.uri");
		}
	} else {
		targetURL = msg.get("message.uri");
	}

	// AdresseIp 
	var ipSource = msg.get("request.ip.source");
	var ipOrigin = msg.get("request.ip.origin");

	if (!ipSource) {
		ipSource = msg.get("http.request.clientaddr");
		ipOrigin = msg.get("http.request.clientaddr");
	} 

	// Récupération d'un message d'erreur
	// Attention, par defaut, il y a un message d'erreur standard
	var detail = msg.get("msg.error.reason");
	if (!detail) {
		detail = "Aucun détail";
	} 

	// Extraction du code statut
	var codeStatus =  msg.get("http.response.status") ? msg.get("http.response.status") : msg.get("msg.error.status");

	if (codeStatus) {
		codeStatus	= String(codeStatus);

		if (codeStatus.substring(0,1) === "2" || codeStatus.substring(0,1) ==="3"  ) {
			detail = "Aucun détail";
		}
	}	


	var trace = "TECHNIQUE#STAT_DUREE_CIRCUIT#" + dateEndDisplay + "#" + ipSource  + "#" + ipOrigin  + "#" + msg.get("http.request.verb")  + "#" + targetURL  + "#" + msg.get("id")  + "#" + msg.get("request.id") + "#" + userid + "#" + codeStatus  + "#" + totalTimeMs  + "#" + detail;
	msg.put("trace.technique", trace);

	return true;
}

package icdc

import java.text.DateFormat
import java.text.SimpleDateFormat

import com.vordel.circuit.Message
import com.vordel.el.Selector

import groovy.transform.Field

@Field private static final DateFormat DATE_FORMAT =  new SimpleDateFormat("dd/MMMMM/yyyy:HH:mm:ss SSSS", Locale.FRANCE);

@Field private static final Selector<String> AUDIT_ACTION = new Selector<String>(String.format("\$%s", "{audit.action}"), String.class);
@Field private static final Selector<String> AUDIT_STATUS = new Selector<String>(String.format("\$%s", "{audit.status}"), String.class);
@Field private static final Selector<String> AUDIT_DETAIL = new Selector<String>(String.format("\$%s", "{audit.detail}"), String.class);
@Field private static final Selector<String> AUDIT_PROVIDER = new Selector<String>(String.format("\$%s", "{audit.provider}"), String.class);

@Field private static final Selector<String> MESSAGE_ID = new Selector<String>(String.format("\$%s", "{id}"), String.class);
@Field private static final Selector<String> MESSAGE_URI = new Selector<String>(String.format("\$%s", "{message.uri}"), String.class);
@Field private static final Selector<String> SRC_IP = new Selector<String>(String.format("\$%s", "{request.ip.source}"), String.class);
@Field private static final Selector<String> ORIGIN_IP = new Selector<String>(String.format("\$%s", "{request.ip.origin}"), String.class);

@Field private static final Selector<String> SUBJECT = new Selector<String>(String.format("\$%s", "{authentication.subject.id}"), String.class);
@Field private static final Selector<String> ERROR_MESSAGE = new Selector<String>(String.format("\$%s", "{msg.error.reason}"), String.class);

@Field private static final Selector<String> REQUEST_ID = new Selector<String>(String.format("\$%s", "{request.id}"), String.class);
@Field private static final Selector<String> REQUEST_CLIENT_ADDR = new Selector<String>(String.format("\$%s", "{http.request.clientaddr}"), String.class);


boolean invoke(Message msg) {
	long now = System.currentTimeMillis();

	String dateEndDisplay = DATE_FORMAT.format(now)
	String action = AUDIT_ACTION.substitute(msg);
	String status = AUDIT_STATUS.substitute(msg);
	
	if ((action == null) || action.isEmpty()) {
		action = MESSAGE_URI.substitute(msg);
		status = "1";
	}
	
	String provider = AUDIT_PROVIDER.substitute(msg);
	String detail = AUDIT_DETAIL.substitute(msg);
	String userid = SUBJECT.substitute(msg);
	
	if ((userid != null) && userid.isEmpty()) {
		userid = null;
	}
	
	if ((detail == null) || detail.isEmpty()) {
		detail = ERROR_MESSAGE.substitute(msg);
		
		if ((detail == null) || detail.isEmpty()) {
			detail = "Aucun détail";
		}
	}

	String ipSource = SRC_IP.substitute(msg);
	String ipOrigin = ORIGIN_IP.substitute(msg);

	if (ipSource == null) {
		ipSource = REQUEST_CLIENT_ADDR.substitute(msg);
		ipOrigin = REQUEST_CLIENT_ADDR.substitute(msg);
	}
	
	String trace = String.format("AUDIT#SPS#%s#%s#%s#%s#%s#%s#%s#%s#%s#%s",
		dateEndDisplay,
		ipSource,
		ipOrigin,
		MESSAGE_ID.substitute(msg),
		REQUEST_ID.substitute(msg),
		action,
		status,
		provider,
		userid,
		detail);

	msg.put("piste.audit", trace)
	
	return true;
}

package icdc

import java.text.DateFormat
import java.text.SimpleDateFormat

import com.vordel.circuit.Message
import com.vordel.el.Selector

import groovy.transform.Field

@Field private static final DateFormat DATE_FORMAT =  new SimpleDateFormat("dd/MMMMM/yyyy:HH:mm:ss SSSS", Locale.FRANCE);

@Field private static final Selector<Long> START_TIME = new Selector<Long>(String.format("\$%s", "{technique.stat.start}"), Long.class);
@Field private static final Selector<Long> RECEPTION_TIME = new Selector<Long>(String.format("\$%s", "{message.reception_time}"), Long.class);

@Field private static final Selector<String> SUBJECT = new Selector<String>(String.format("\$%s", "{authentication.subject.id}"), String.class);

@Field private static final Selector<String> SERVICE_NAME = new Selector<String>(String.format("\$%s", "{service.name}"), String.class);
@Field private static final Selector<String> API_NAME = new Selector<String>(String.format("\$%s", "{api.name}"), String.class);
@Field private static final Selector<String> API_METHOD_NAME = new Selector<String>(String.format("\$%s", "{api.method.name}"), String.class);
@Field private static final Selector<String> API_METHOD_PATH = new Selector<String>(String.format("\$%s", "{api.method.path}"), String.class);

@Field private static final Selector<String> MESSAGE_URI = new Selector<String>(String.format("\$%s", "{message.uri}"), String.class);
@Field private static final Selector<String> SRC_IP = new Selector<String>(String.format("\$%s", "{request.ip.source}"), String.class);
@Field private static final Selector<String> ORIGIN_IP = new Selector<String>(String.format("\$%s", "{request.ip.origin}"), String.class);

@Field private static final Selector<String> ERROR_MESSAGE = new Selector<String>(String.format("\$%s", "{msg.error.reason}"), String.class);

@Field private static final Selector<Integer> HTTP_STATUS = new Selector<Integer>(String.format("\$%s", "{http.response.status}"), Integer.class);
@Field private static final Selector<Integer> ERROR_STATUS = new Selector<Integer>(String.format("\$%s", "{msg.error.status}"), Integer.class);

@Field private static final Selector<String> REQUEST_VERB = new Selector<String>(String.format("\$%s", "{http.request.verb}"), String.class);
@Field private static final Selector<String> REQUEST_CLIENT_ADDR = new Selector<String>(String.format("\$%s", "{http.request.clientaddr}"), String.class);
@Field private static final Selector<String> MESSAGE_ID = new Selector<String>(String.format("\$%s", "{id}"), String.class);
@Field private static final Selector<String> REQUEST_ID = new Selector<String>(String.format("\$%s", "{request.id}"), String.class);

boolean invoke(Message msg) {
	long now = System.currentTimeMillis();

	String dateEndDisplay = DATE_FORMAT.format(now);
	Long startTime = START_TIME.substitute(msg);

	if (startTime) {
		startTime = RECEPTION_TIME.substitute(msg);
	}

	long totalTimeMs = now - startTime.longValue();

	msg.put("request.reponse.time", totalTimeMs);

	String userid = SUBJECT.substitute(msg);
	String apiName = API_NAME.substitute(msg);
	String targetURL = null;

	if (apiName != null) {
		String serviceName = SERVICE_NAME.substitute(msg);

		if (serviceName != null) {
			targetURL = String.format("%s - %s - %s", serviceName, API_METHOD_NAME.substitute(msg), API_METHOD_PATH.substitute(msg));
		}
	}

	if (targetURL == null) {
		targetURL = MESSAGE_URI.substitute(msg);
	}

	String ipSource = SRC_IP.substitute(msg);
	String ipOrigin = ORIGIN_IP.substitute(msg);

	if (ipSource == null) {
		ipSource = REQUEST_CLIENT_ADDR.substitute(msg);
		ipOrigin = REQUEST_CLIENT_ADDR.substitute(msg);
	}

	Integer codeStatus = HTTP_STATUS.substitute(msg);
	String detail = null
	
	if (codeStatus == null) {
		codeStatus = ERROR_STATUS.substitute(msg);
	}

	if ((codeStatus == null) || (codeStatus < 200) || (codeStatus >= 400)) {
		detail = ERROR_MESSAGE.substitute(msg);
	}

	if (detail == null) {
		detail = "Aucun détail";
	}

	String trace = String.format("TECHNIQUE#STAT_DUREE_CIRCUIT#%s#%s#%s#%s#%s#%s#%s#%s#%d#%d#%s",
			dateEndDisplay,
			ipSource,
			ipOrigin,
			REQUEST_VERB.substitute(msg),
			targetURL,
			MESSAGE_ID.substitute(msg),
			REQUEST_ID.substitute(msg),
			userid,
			codeStatus,
			totalTimeMs,
			detail);
	msg.put("trace.technique", trace);

	return true;
}

