package icdc

import com.vordel.circuit.Message
import com.vordel.circuit.oauth.token.OAuth2AccessToken
import com.vordel.circuit.oauth.token.OAuth2Authentication

boolean invoke(Message msg) {
	Set<String> scopesRequested = msg.get("scopes.required");
	
	msg.put("scopes.requested.converted",  asString(scopesRequested.iterator()));
	
	return true;
}

public static String asString(Iterator<String> iterator) {
	String scope = null;

	if ((iterator != null) && iterator.hasNext()) {
		StringBuilder builder = new StringBuilder().append(iterator.next());
		Set<String> used = new HashSet<String>();

		while (iterator.hasNext()) {
			String next = iterator.next();
			
			if ((next != null) && (!next.isEmpty()) && used.add(next)) {
				/* XXX scope separator should be a space */
				/*builder.append(' ');*/
				builder.append(',');
				builder.append(next);
			}
		}

		scope = builder.toString();
	}

	return scope;
}

importPackage(Packages.com.vordel.trace);
importPackage(Packages.com.vordel.common.base64);

function invoke(msg)         
{        
    var base64EncodedData = msg.get("jwt.token.b64");

    Trace.debug("base64EncodedData" + ":" + base64EncodedData );
    var dataDecoded = Decoder.decodeToString(base64EncodedData);

    //obtention d'un jeton JWT : uniquement la partie PAYLOAD

    msg.put("jwt.token", dataDecoded  );
    return true;        
}

importPackage(Packages.com.vordel.jose.*);
importPackage(Packages.com.vordel.jose.crypto.*);
importPackage(Packages.com.vordel.jose.jwt.*);
importPackage(Packages.java.security);
importPackage(Packages.com.vordel.trace);
importPackage(Packages.java.util);
importPackage(Packages.com.vordel.common.base64.*);
importPackage(org.apache.commons.codec.binary.*);

function invoke(msg)         
{        
	//Algorithm.HS256
	var headerJWT =  "ew0KICAiYWxnIjogIkhTMjU2IiwNCiAgInR5cCI6ICJKV1QiDQp9DQo=";
	var dataJWT = msg.get("jwt.token.backend");

	//On encode en base64 le payload
	var base64EncodedData = com.vordel.common.base64.Encoder.encodeNoLineBreaks(dataJWT.getBytes());

	var data =  new java.lang.String ( headerJWT + "." + base64EncodedData) ;

	//Extraction de la secret key associée au SPS 
	var secretKey = new java.lang.String(msg.get("jwt.secret.key"));
	var secretHmac = new com.vordel.jose.crypto.HmacSHA256Signer(secretKey.trim().getBytes());

	var signature = secretHmac.sign( data.getBytes());	
	signature = org.apache.commons.codec.binary.Base64.encodeBase64URLSafeString(signature )

	msg.put("jwt.signed", data + "." +signature );

	return true;        
}

importPackage(Packages.com.vordel.jose.*);
importPackage(Packages.com.vordel.jose.crypto.*);
importPackage(Packages.com.vordel.jose.jwt.*);
importPackage(Packages.java.security);
importPackage(Packages.com.vordel.trace);
importPackage(Packages.java.util);
importPackage(Packages.java.lang);


function invoke(msg)         {            

	//Extraction identifiant SPS
	var iss = msg.get("jwt.iss");

	//Extraction de la secret key associée au SPS 
	var secretKey = new java.lang.String(msg.get("jwt.secret.key"));
	secretKey = secretKey.trim();

	//Création du jeton
	var header = new com.vordel.jose.jwt.Header();
	header.setAlgorithm("HS256");
	header.setType("JWT");
		
	var payload = new com.vordel.jose.jwt.JWT.Claim();

	payload.setIssuer(iss);
	payload.setAudience(msg.get("jwt.aud"));
	payload.addExtension("sub", msg.get("codeAramis"));

	var jti = java.util.UUID.randomUUID();
	payload.setJwtId(jti);

	var now = Math.round(new Date().getTime() / 1000);
	
	var delai = Integer.parseInt(msg.get("jwt.exp"));

	payload.setIssuedAtTimeSeconds(now);	
	payload.setExpirationTimeSeconds(now + delai );
   
	var jwt = new com.vordel.jose.jwt.JWT(header, payload);
	var jws;
	
	try {

		// Création d'un jeton JWT signé
		jws = com.vordel.jose.jwt.JWS.sign(jwt, secretKey.getBytes(), com.vordel.jose.crypto.SignatureAlgorithm.HS256, com.vordel.jose.JWTUtils.getObjectMapper());
			
		msg.put("jwt.signed", jws.generateSignatureString() );

		return true;

	}  catch (e) {
		Trace.error(">>>Exception : " +e);
		return false;
	}
	
}

function invoke(msg)         {            
	
	//Récupération des scopes requis HashSet
	//Attention : Obtention d'une chaine du type "[urn:cdc:poc:libraire:api:1.0,urn:cdc:poc:libraire:api:1.0,urn:cdc:poc:libraire:api:1.0]"

	var scopesRequested = msg.get("scopes.required");
	var scopesRequestedConverted=""+scopesRequested ;
	msg.put("scopes.requested.converted", scopesRequestedConverted.substring(1, scopesRequestedConverted.length-1));

	return true;         
}

package icdc

import com.nimbusds.jose.JOSEException
import com.nimbusds.jose.JOSEObjectType
import com.nimbusds.jose.JWSAlgorithm
import com.nimbusds.jose.JWSHeader
import com.nimbusds.jose.JWSSigner
import com.nimbusds.jose.crypto.MACSigner
import com.nimbusds.jwt.JWTClaimsSet
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message

boolean invoke(Message msg) {
	String iss = msg.get("jwt.iss");
	
	/* build the JOSE header */
	JWSHeader header = new JWSHeader.Builder(JWSAlgorithm.HS256).type(JOSEObjectType.JWT).build();
	
	/* retrieve, trim and pad secret key */
	byte[] secretKey = padKey(header.getAlgorithm(), ((String) msg.get("jwt.secret.key")).trim().getBytes());
	JWSSigner signer = new MACSigner(secretKey);

	/* generate claim set from existing data */
	JWTClaimsSet claims = JWTClaimsSet.parse(msg.get("jwt.token.backend"));

	/* generate JWT object and sign it */	
	SignedJWT jwt = new SignedJWT(header, claims);
	
	jwt.sign(signer);
	
	msg.put("jwt.signed", jwt.serialize());
	
	return true;
}

public static final byte[] padKey(JWSAlgorithm algorithm, byte[] key) throws JOSEException {
	int requested = MACSigner.getMinRequiredSecretLength(algorithm);
	int available = key.length * 8;

	if (requested > available) {
		/* pad the key with '0' or trim it if needed */
		ByteArrayOutputStream out = new ByteArrayOutputStream();
		ByteArrayInputStream inp = new ByteArrayInputStream(key);

		for (int read = -1; (requested > 0) && (read = inp.read()) != -1; requested -= 8) {
			out.write(read);
		}

		for (; requested > 0; requested -= 8) {
			out.write(0);
		}

		key = out.toByteArray();
	}

	return key;
}

package icdc

import com.nimbusds.jose.JOSEException
import com.nimbusds.jose.JOSEObjectType
import com.nimbusds.jose.JWSAlgorithm
import com.nimbusds.jose.JWSHeader
import com.nimbusds.jose.JWSSigner
import com.nimbusds.jose.crypto.MACSigner
import com.nimbusds.jwt.JWTClaimsSet
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message

boolean invoke(Message msg) {
	String iss = msg.get("jwt.iss");
	
	/* build the JOSE header */
	JWSHeader header = new JWSHeader.Builder(JWSAlgorithm.HS256).type(JOSEObjectType.JWT).build();
	
	/* retrieve, trim and pad secret key */
	byte[] secretKey = padKey(header.getAlgorithm(), ((String) msg.get("jwt.secret.key")).trim().getBytes());
	JWSSigner signer = new MACSigner(secretKey);

	/* generate claim set */
	JWTClaimsSet.Builder claims = new JWTClaimsSet.Builder();
	long now = System.currentTimeMillis();
	long delay = Long.parseLong(msg.get("jwt.exp")) * 1000L;
	
	claims = claims.issuer(msg.get("jwt.iss"));
	claims = claims.audience(msg.get("jwt.aud"));
	claims = claims.subject(msg.get("codeAramis"));
	claims = claims.jwtID(UUID.randomUUID().toString());
	claims = claims.issueTime(new Date(now)).expirationTime(new Date(now + delay));

	/* generate JWT object and sign it */	
	SignedJWT jwt = new SignedJWT(header, claims.build());
	
	jwt.sign(signer);
	
	msg.put("jwt.signed", jwt.serialize());
	
	return true;
}

public static final byte[] padKey(JWSAlgorithm algorithm, byte[] key) throws JOSEException {
	int requested = MACSigner.getMinRequiredSecretLength(algorithm);
	int available = key.length * 8;

	if (requested > available) {
		/* pad the key with '0' or trim it if needed */
		ByteArrayOutputStream out = new ByteArrayOutputStream();
		ByteArrayInputStream inp = new ByteArrayInputStream(key);

		for (int read = -1; (requested > 0) && (read = inp.read()) != -1; requested -= 8) {
			out.write(read);
		}

		for (; requested > 0; requested -= 8) {
			out.write(0);
		}

		key = out.toByteArray();
	}

	return key;
}

