}

importPackage(Packages.com.vordel.common.base64);

function invoke(msg)         
{        
    var base64EncodedData = msg.get("jwt.token.b64");
    var dataDecoded = Decoder.decodeToString(base64EncodedData);

    //obtention d'un jeton JWT : uniquement la partie PAYLOAD

    msg.put("jwt.token", dataDecoded  );
    return true;        
}

function invoke(msg)         {            
	
	//Récupération des scopes requis HashSet
	//Attention : Obtention d'une chaine du type "[urn:cdc:poc:libraire:api:1.0,urn:cdc:poc:libraire:api:1.0,urn:cdc:poc:libraire:api:1.0]"

	var scopesRequested = msg.get("accesstoken.authn");
	var authScope = new String(scopesRequested.getAuthorizationRequest().getScope());

	msg.put("scopes.requested.converted",  authScope.substring(1, authScope.length-1));

	//Récupération des scopes obtenus
	//Attention : Obtention d'une chaine du type "[urn:cdc:poc:libraire:api:1.0,urn:cdc:poc:libraire:api:1.0,urn:cdc:poc:libraire:api:1.0]"
	var accessToken = msg.get("accesstoken");
	var scopesObtained = new String(accessToken.getScope());
	
	//Transformation sous la forme d'un tableau
	msg.put("scopes.obtained.converted",  scopesObtained.substring(1, scopesObtained.length-1));

	return true;         
}

package icdc

import com.vordel.circuit.Message
import com.vordel.circuit.oauth.token.OAuth2AccessToken
import com.vordel.circuit.oauth.token.OAuth2Authentication

boolean invoke(Message msg) {
	OAuth2Authentication authn = msg.get("accesstoken.authn");
	OAuth2AccessToken accessToken = msg.get("accesstoken");
	
	msg.put("scopes.requested.converted",  asString(authn.getAuthorizationRequest().getScope().iterator()));
	msg.put("scopes.obtained.converted",  asString(accessToken.getScope().iterator()));
	
	return true;
}

public static String asString(Iterator<String> iterator) {
	String scope = null;

	if ((iterator != null) && iterator.hasNext()) {
		StringBuilder builder = new StringBuilder().append(iterator.next());
		Set<String> used = new HashSet<String>();

		while (iterator.hasNext()) {
			String next = iterator.next();
			
			if ((next != null) && (!next.isEmpty()) && used.add(next)) {
				/* XXX scope separator should be a space */
				/*builder.append(' ');*/
				builder.append(',');
				builder.append(next);
			}
		}

		scope = builder.toString();
	}

	return scope;
}

package icdc.refactored

import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ObjectNode
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.oauth.token.OAuth2AccessToken
import com.vordel.circuit.oauth.token.OAuth2Authentication
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody

import groovy.transform.Field

@Field private static final Selector<OAuth2Authentication> OAUTH_AUTHN = SelectorResource.fromExpression("accesstoken.authn", OAuth2Authentication.class);
@Field private static final Selector<OAuth2AccessToken> OAUTH_AT = SelectorResource.fromExpression("accesstoken", OAuth2AccessToken.class);
@Field private static final Selector<JSONBody> CONTENT_BODY = SelectorResource.fromExpression(MessageProperties.CONTENT_BODY, JSONBody.class);

@Field private static final Selector<String> JWT_AUDIENCE = SelectorResource.fromExpression("jwt.aud", String.class);
@Field private static final Selector<String> CDC_APPLICATION_ID = SelectorResource.fromExpression("oauth.client.details.getApplicationID()", String.class);
@Field private static final Selector<String> CDC_APPLICATION_NAME = SelectorResource.fromExpression("oauth.client.details.getName()", String.class);

@Field private static final Base64.Decoder DECODER = Base64.getDecoder()
@Field private static final ObjectMapper MAPPER = new ObjectMapper();

boolean invoke(Message msg) {
	OAuth2Authentication authn = OAUTH_AUTHN.substitute(msg);
	OAuth2AccessToken accessToken = OAUTH_AT.substitute(msg);
	JSONBody body = CONTENT_BODY.substitute(msg); /* should be an empty Json Object */

	String subject = authn.getUserAuthentication();
	String payload = decodeJWTPayload(msg, accessToken.getAdditionalInformation().get("jwttoken"));
	String requested = asString(authn.getAuthorizationRequest().getScope().iterator());
	String obtained = asString(accessToken.getScope().iterator());

	msg.put(MessageProperties.AUTHN_SUBJECT_ID, subject);
	msg.put("scopes.requested.converted", requested);
	msg.put("scopes.obtained.converted", obtained);

	ObjectNode claims = body.getJSON();

	claims.setAll((ObjectNode) MAPPER.readTree(payload));

	putJsonString(msg, claims, "aud", JWT_AUDIENCE);
	putJsonString(msg, claims, "urn:cdc:claims:applicationId", CDC_APPLICATION_ID);
	putJsonString(msg, claims, "urn:cdc:claims:applicationName", CDC_APPLICATION_NAME);

	claims.put("urn:cdc:claims:scopeRequested", requested);
	claims.put("urn:cdc:claims:scopeObtained", obtained);

	return true;
}

public static void putJsonString(Message msg, ObjectNode claims, String name, Selector<String> selector) {
	String value = selector.substitute(msg);

	if (value != null) {
		claims.put(name, value);
	}
}

public static String decodeJWTPayload(Message msg, String encoded) {
	String payload = null;

	if (encoded != null) {
		msg.put("jwt.token.b64", encoded);
		msg.put("jwt.token", payload = new String(DECODER.decode(encoded)));
	}

	return payload;
}

public static String asString(Iterator<String> iterator) {
	String scope = null;

	if ((iterator != null) && iterator.hasNext()) {
		StringBuilder builder = new StringBuilder().append(iterator.next());
		Set<String> used = new HashSet<String>();

		while (iterator.hasNext()) {
			String next = iterator.next();

			if ((next != null) && (!next.isEmpty()) && used.add(next)) {
				/* XXX scope separator should be a space */
				/*builder.append(' ');*/
				builder.append(',');
				builder.append(next);
			}
		}

		scope = builder.toString();
	}

	return scope;
}

package icdc.refactored

import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ObjectNode
import com.nimbusds.jwt.JWTClaimsSet
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody

import groovy.transform.Field

@Field private static final Selector<JSONBody> CONTENT_BODY = SelectorResource.fromExpression(MessageProperties.CONTENT_BODY, JSONBody.class);

@Field private static final Selector<String> APPLICATION_NAME = SelectorResource.fromExpression("authentication.application.name", String.class);
@Field private static final Selector<String> ARAMIS_CODE = SelectorResource.fromExpression("apimgmt.application.codeAramis.toLowerCase()", String.class);

@Field private static final Selector<String> JWT_ISS = SelectorResource.fromExpression("jwt.iss", String.class);
@Field private static final Selector<Long> JWT_EXP = SelectorResource.fromExpression("jwt.exp", Long.class);

/* Utils */
@Field private static final ObjectMapper MAPPER = new ObjectMapper();

boolean invoke(Message msg) {
	JSONBody body = CONTENT_BODY.substitute(msg); /* should be an empty Json Object */
	ObjectNode claims = body.getJSON();

	String subject = APPLICATION_NAME.substitute(msg);
	String aramisCode = ARAMIS_CODE.substitute(msg);
	
	/* generate claim set */
	JWTClaimsSet.Builder builder = new JWTClaimsSet.Builder();
	long now = System.currentTimeMillis();
	long delay = JWT_EXP.substitute(msg);
	
	builder = builder.issuer(JWT_ISS.substitute(msg));
	builder = builder.subject(aramisCode);
	builder = builder.jwtID(UUID.randomUUID().toString());
	builder = builder.issueTime(new Date(now)).expirationTime(new Date(now + delay));

	ObjectNode template = MAPPER.readTree(builder.build().toString());
	
	claims.setAll(template);

	msg.put(MessageProperties.AUTHN_SUBJECT_ID, subject);
	msg.put("codeAramis", aramisCode)
	
	return true;
}

package icdc.refactored

import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ObjectNode
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.oauth.token.OAuth2AccessToken
import com.vordel.circuit.oauth.token.OAuth2Authentication
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody

import groovy.transform.Field

@Field private static final Selector<JSONBody> CONTENT_BODY = SelectorResource.fromExpression(MessageProperties.CONTENT_BODY, JSONBody.class);

/* Internal Access Token selectors */
@Field private static final Selector<OAuth2Authentication> OAUTH_AUTHN = SelectorResource.fromExpression("accesstoken.authn", OAuth2Authentication.class);
@Field private static final Selector<OAuth2AccessToken> OAUTH_AT = SelectorResource.fromExpression("accesstoken", OAuth2AccessToken.class);
@Field private static final Selector<String> CDC_APPLICATION_ID = SelectorResource.fromExpression("oauth.client.details.getApplicationID()", String.class);
@Field private static final Selector<String> CDC_APPLICATION_NAME = SelectorResource.fromExpression("oauth.client.details.getName()", String.class);

/* External Access Token Selectors */
@Field private static final Selector<String> EXTERNAL_TOKEN_VALID = SelectorResource.fromExpression("oauth.token.valid", String.class);
@Field private static final Selector<String> EXTERNAL_TOKEN_BASE64 = SelectorResource.fromExpression("oauth.token.jwttoken", String.class);
@Field private static final Selector<String> EXTERNAL_TOKEN_SUBJECT = SelectorResource.fromExpression("oauth.token.user_id", String.class);
@Field private static final Selector<String> EXTERNAL_TOKEN_CLIENTID = SelectorResource.fromExpression("oauth.token.client_id", String.class);
@Field private static final Selector<String> EXTERNAL_TOKEN_APPCODE = SelectorResource.fromExpression("authentication.application.codeAramis.toLowerCase()", String.class);
@Field private static final Selector<String> EXTERNAL_TOKEN_APPNAME = SelectorResource.fromExpression("authentication.application.name", String.class);
@Field private static final Selector<Set<String>> EXTERNAL_REQUIRED_SCOPES = SelectorResource.fromExpression("scopes.required", Set.class);
@Field private static final Selector<String> EXTERNAL_OBTAINED_SCOPES = SelectorResource.fromExpression("oauth.token.scopes", String.class);

/* Common Selectors */
//@Field private static final Selector<String> JWT_AUDIENCE = SelectorResource.fromExpression("jwt.aud", String.class);

/* Utils */
@Field private static final Base64.Decoder DECODER = Base64.getDecoder();
@Field private static final ObjectMapper MAPPER = new ObjectMapper();


boolean invoke(Message msg) {
	OAuth2AccessToken accessToken = OAUTH_AT.substitute(msg);
	boolean result = false;

	String subject = null;
	//String audience = JWT_AUDIENCE.substitute(msg);
	String applicationID = null;
	String applicationCode = null;
	String applicationName = null;
	String requestedScopes = null;
	String obtainedScopes = null;

	JSONBody body = CONTENT_BODY.substitute(msg); /* should be an empty Json Object */
	ObjectNode claims = body.getJSON();
	ObjectNode template = null;

	if (accessToken != null) {
		/* regular access token case */
		OAuth2Authentication authn = OAUTH_AUTHN.substitute(msg);
		String payload = decodeJWTPayload(msg, accessToken.getAdditionalInformation().get("jwttoken"));

		subject = authn.getUserAuthentication();
		applicationID = CDC_APPLICATION_ID.substitute(msg);
		applicationName = CDC_APPLICATION_NAME.substitute(msg);
		requestedScopes = asString(authn.getAuthorizationRequest().getScope());
		obtainedScopes = asString(accessToken.getScope());

		template = MAPPER.readTree(payload);

		/* set message attributes for compatibility */
		msg.put("scopes.requested.converted", requestedScopes);
		msg.put("scopes.obtained.converted", obtainedScopes);

		result = true;
	} else if (Boolean.valueOf(EXTERNAL_TOKEN_VALID.substitute(msg))) {
		/* external access token case */
		String payload = decodeJWTPayload(msg, EXTERNAL_TOKEN_BASE64.substitute(msg));

		subject = EXTERNAL_TOKEN_SUBJECT.substitute(msg);
		applicationID = EXTERNAL_TOKEN_CLIENTID.substitute(msg);
		applicationCode = EXTERNAL_TOKEN_APPCODE.substitute(msg);
		applicationName = EXTERNAL_TOKEN_APPNAME.substitute(msg);
		requestedScopes = asString(EXTERNAL_REQUIRED_SCOPES.substitute(msg));
		obtainedScopes = EXTERNAL_OBTAINED_SCOPES.substitute(msg);

		template = MAPPER.readTree(payload);

		/* set message attributes for compatibility */
		msg.put("scopes.requested.converted", requestedScopes);

		result = true;
	}

	if (result) {
		if (subject != null) {
			msg.put(MessageProperties.AUTHN_SUBJECT_ID, subject);
		}

		if (template != null) {
			claims.setAll(template);
		}

		//setStringClaim(claims, "aud", audience);
		setStringClaim(claims, "urn:cdc:claims:applicationId", applicationID);
		setStringClaim(claims, "urn:cdc:claims:applicationCodeApp", applicationCode);
		setStringClaim(claims, "urn:cdc:claims:applicationName", applicationName);
		setStringClaim(claims, "urn:cdc:claims:scopeRequested", requestedScopes);
		setStringClaim(claims, "urn:cdc:claims:scopeObtained", requestedScopes);
	}

	return result;
}

public static void setStringClaim(ObjectNode claims, String name, String value) {
	if (value != null) {
		claims.put(name, value);
	}
}

public static String decodeJWTPayload(Message msg, String encoded) {
	String payload = null;

	if (encoded != null) {
		msg.put("jwt.token.b64", encoded);
		msg.put("jwt.token", payload = new String(DECODER.decode(encoded)));
	}

	return payload;
}

public static String asString(Set<String> scopes) {
	String scope = null;

	if (scopes != null) {
		Iterator<String> iterator = scopes.iterator();

		if ((iterator != null) && iterator.hasNext()) {
			StringBuilder builder = new StringBuilder().append(iterator.next());
			Set<String> used = new HashSet<String>();

			while (iterator.hasNext()) {
				String next = iterator.next();

				if ((next != null) && (!next.isEmpty()) && used.add(next)) {
					/* XXX scope separator should be a space */
					/*builder.append(' ');*/
					builder.append(',');
					builder.append(next);
				}
			}

			scope = builder.toString();
		}
	}

	return scope;
}

importPackage(Packages.com.vordel.trace);
importPackage(Packages.com.vordel.common.base64);

function invoke(msg)         
{        
    var base64EncodedData = msg.get("jwt.token.b64");

    Trace.debug("base64EncodedData" + ":" + base64EncodedData );
    var dataDecoded = Decoder.decodeToString(base64EncodedData);

    //obtention d'un jeton JWT : uniquement la partie PAYLOAD

    msg.put("jwt.token", dataDecoded  );
    return true;        
}

package icdc

import com.nimbusds.jose.JOSEException
import com.nimbusds.jose.JOSEObjectType
import com.nimbusds.jose.JWSAlgorithm
import com.nimbusds.jose.JWSHeader
import com.nimbusds.jose.JWSSigner
import com.nimbusds.jose.crypto.MACSigner
import com.nimbusds.jwt.JWTClaimsSet
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message

boolean invoke(Message msg) {
	String iss = msg.get("jwt.iss");
	
	/* build the JOSE header */
	JWSHeader header = new JWSHeader.Builder(JWSAlgorithm.HS256).type(JOSEObjectType.JWT).build();
	
	/* retrieve, trim and pad secret key */
	byte[] secretKey = padKey(header.getAlgorithm(), ((String) msg.get("jwt.secret.key")).trim().getBytes());
	JWSSigner signer = new MACSigner(secretKey);

	/* generate claim set from existing data */
	JWTClaimsSet claims = JWTClaimsSet.parse(msg.get("jwt.token.backend"));

	/* generate JWT object and sign it */	
	SignedJWT jwt = new SignedJWT(header, claims);
	
	jwt.sign(signer);
	
	msg.put("jwt.signed", jwt.serialize());
	
	return true;
}

public static final byte[] padKey(JWSAlgorithm algorithm, byte[] key) throws JOSEException {
	int requested = MACSigner.getMinRequiredSecretLength(algorithm);
	int available = key.length * 8;

	if (requested > available) {
		/* pad the key with '0' or trim it if needed */
		ByteArrayOutputStream out = new ByteArrayOutputStream();
		ByteArrayInputStream inp = new ByteArrayInputStream(key);

		for (int read = -1; (requested > 0) && (read = inp.read()) != -1; requested -= 8) {
			out.write(read);
		}

		for (; requested > 0; requested -= 8) {
			out.write(0);
		}

		key = out.toByteArray();
	}

	return key;
}

package icdc

import com.vordel.circuit.Message
import com.vordel.circuit.oauth.token.OAuth2AccessToken
import com.vordel.circuit.oauth.token.OAuth2Authentication

boolean invoke(Message msg) {
	Set<String> scopesRequested = msg.get("scopes.required");
	
	msg.put("scopes.requested.converted",  asString(scopesRequested.iterator()));
	
	return true;
}

public static String asString(Iterator<String> iterator) {
	String scope = null;

	if ((iterator != null) && iterator.hasNext()) {
		StringBuilder builder = new StringBuilder().append(iterator.next());
		Set<String> used = new HashSet<String>();

		while (iterator.hasNext()) {
			String next = iterator.next();
			
			if ((next != null) && (!next.isEmpty()) && used.add(next)) {
				/* XXX scope separator should be a space */
				/*builder.append(' ');*/
				builder.append(',');
				builder.append(next);
			}
		}

		scope = builder.toString();
	}

	return scope;
}

importPackage(Packages.com.vordel.jose.*);
importPackage(Packages.com.vordel.jose.crypto.*);
importPackage(Packages.com.vordel.jose.jwt.*);
importPackage(Packages.java.security);
importPackage(Packages.com.vordel.trace);
importPackage(Packages.java.util);
importPackage(Packages.java.lang);


function invoke(msg)         {            

	//Extraction identifiant SPS
	var iss = msg.get("jwt.iss");

	//Extraction de la secret key associée au SPS 
	var secretKey = new java.lang.String(msg.get("jwt.secret.key"));
	secretKey = secretKey.trim();

	//Création du jeton
	var header = new com.vordel.jose.jwt.Header();
	header.setAlgorithm("HS256");
	header.setType("JWT");
		
	var payload = new com.vordel.jose.jwt.JWT.Claim();

	payload.setIssuer(iss);
	payload.setAudience(msg.get("jwt.aud"));
	payload.addExtension("sub", msg.get("codeAramis"));

	var jti = java.util.UUID.randomUUID();
	payload.setJwtId(jti);

	var now = Math.round(new Date().getTime() / 1000);
	
	var delai = Integer.parseInt(msg.get("jwt.exp"));

	payload.setIssuedAtTimeSeconds(now);	
	payload.setExpirationTimeSeconds(now + delai );
   
	var jwt = new com.vordel.jose.jwt.JWT(header, payload);
	var jws;
	
	try {

		// Création d'un jeton JWT signé
		jws = com.vordel.jose.jwt.JWS.sign(jwt, secretKey.getBytes(), com.vordel.jose.crypto.SignatureAlgorithm.HS256, com.vordel.jose.JWTUtils.getObjectMapper());
			
		msg.put("jwt.signed", jws.generateSignatureString() );

		return true;

	}  catch (e) {
		Trace.error(">>>Exception : " +e);
		return false;
	}
	
}

function invoke(msg)         {            
	
	//Récupération des scopes requis HashSet
	//Attention : Obtention d'une chaine du type "[urn:cdc:poc:libraire:api:1.0,urn:cdc:poc:libraire:api:1.0,urn:cdc:poc:libraire:api:1.0]"

	var scopesRequested = msg.get("scopes.required");
	var scopesRequestedConverted=""+scopesRequested ;
	msg.put("scopes.requested.converted", scopesRequestedConverted.substring(1, scopesRequestedConverted.length-1));

	return true;         
}

importPackage(Packages.com.vordel.jose.*);
importPackage(Packages.com.vordel.jose.crypto.*);
importPackage(Packages.com.vordel.jose.jwt.*);
importPackage(Packages.java.security);
importPackage(Packages.com.vordel.trace);
importPackage(Packages.java.util);
importPackage(Packages.com.vordel.common.base64.*);
importPackage(org.apache.commons.codec.binary.*);

function invoke(msg)         
{        
	//Algorithm.HS256
	var headerJWT =  "ew0KICAiYWxnIjogIkhTMjU2IiwNCiAgInR5cCI6ICJKV1QiDQp9DQo=";
	var dataJWT = msg.get("jwt.token.backend");

	//On encode en base64 le payload
	var base64EncodedData = com.vordel.common.base64.Encoder.encodeNoLineBreaks(dataJWT.getBytes());

	var data =  new java.lang.String ( headerJWT + "." + base64EncodedData) ;

	//Extraction de la secret key associée au SPS 
	var secretKey = new java.lang.String(msg.get("jwt.secret.key"));
	var secretHmac = new com.vordel.jose.crypto.HmacSHA256Signer(secretKey.trim().getBytes());

	var signature = secretHmac.sign( data.getBytes());	
	signature = org.apache.commons.codec.binary.Base64.encodeBase64URLSafeString(signature )

	msg.put("jwt.signed", data + "." +signature );

	return true;        
}

package icdc

import com.nimbusds.jose.JOSEException
import com.nimbusds.jose.JOSEObjectType
import com.nimbusds.jose.JWSAlgorithm
import com.nimbusds.jose.JWSHeader
import com.nimbusds.jose.JWSSigner
import com.nimbusds.jose.crypto.MACSigner
import com.nimbusds.jwt.JWTClaimsSet
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message

boolean invoke(Message msg) {
	String iss = msg.get("jwt.iss");
	
	/* build the JOSE header */
	JWSHeader header = new JWSHeader.Builder(JWSAlgorithm.HS256).type(JOSEObjectType.JWT).build();
	
	/* retrieve, trim and pad secret key */
	byte[] secretKey = padKey(header.getAlgorithm(), ((String) msg.get("jwt.secret.key")).trim().getBytes());
	JWSSigner signer = new MACSigner(secretKey);

	/* generate claim set */
	JWTClaimsSet.Builder claims = new JWTClaimsSet.Builder();
	long now = System.currentTimeMillis();
	long delay = Long.parseLong(msg.get("jwt.exp")) * 1000L;
	
	claims = claims.issuer(msg.get("jwt.iss"));
	claims = claims.audience(msg.get("jwt.aud"));
	claims = claims.subject(msg.get("codeAramis"));
	claims = claims.jwtID(UUID.randomUUID().toString());
	claims = claims.issueTime(new Date(now)).expirationTime(new Date(now + delay));

	/* generate JWT object and sign it */	
	SignedJWT jwt = new SignedJWT(header, claims.build());
	
	jwt.sign(signer);
	
	msg.put("jwt.signed", jwt.serialize());
	
	return true;
}

public static final byte[] padKey(JWSAlgorithm algorithm, byte[] key) throws JOSEException {
	int requested = MACSigner.getMinRequiredSecretLength(algorithm);
	int available = key.length * 8;

	if (requested > available) {
		/* pad the key with '0' or trim it if needed */
		ByteArrayOutputStream out = new ByteArrayOutputStream();
		ByteArrayInputStream inp = new ByteArrayInputStream(key);

		for (int read = -1; (requested > 0) && (read = inp.read()) != -1; requested -= 8) {
			out.write(read);
		}

		for (; requested > 0; requested -= 8) {
			out.write(0);
		}

		key = out.toByteArray();
	}

	return key;
}

