package icdc

import com.vordel.circuit.Message
import com.vordel.trace.Trace

boolean invoke(Message msg) {
	String destination = msg.get("destinationURL");
	String query = msg.get("http.request.rawURI.query");
	int index = destination.indexOf("?");
	
	Trace.debug("destinationURLAvant:"+destination );
	
	if (index > 0) {
		destination.substring(0,index);
	}
	
	if (query != null) {
		/* append query if any */
		destination = String.format("%s?%s", destination, query);
	}
	
	/* normalize URI */
	destination = new URI(destination).normalize().toString();
	
	Trace.debug("destinationURLApres:"+destination );

	msg.put("destinationURL",destination );
	
	return true;
}

