function invoke(msg)         {           
	
	var now = new Date(); 

	msg.put("ws.backend.start.time", now.getTime().toString());

	return true;
}

function invoke(msg)         {           
	
	var kerberosToken = msg.get("ws.trust.spnego.token");
	var hex = [];
	for ( i = 0; i <kerberosToken.length; i++) {
        var current = kerberosToken[i] < 0 ? kerberosToken[i] + 256 : kerberosToken[i];
        hex.push((current >>> 4).toString(16));
        hex.push((current & 0xF).toString(16));
       } 

	//msg.put("kerberos.token", hex.join());

	msg.put("kerberos.token", java.util.Base64.getEncoder().encodeToString(kerberosToken));

	return true;
}

