		msg.put(MessageProperties.HTTP_HEADERS, headers);

		/* set the 302 response */
		msg.put(MessageProperties.HTTP_RSP_STATUS, 302);
		msg.put(MessageProperties.HTTP_RSP_INFO, HTTPPlugin.getResponseText(302));

		result = true;
	}

	return result;
}

package icdc.oauth

import com.vordel.circuit.Message
import com.vordel.circuit.oauth.kps.ApplicationDetails
import com.vordel.common.apiserver.model.Application

boolean invoke(Message msg) {
	ApplicationDetails details = msg.get("oauth.client.details");
	String codeAramis = null;
	
	if (details != null) {
		Application application = details.getApplication();
		Map<String, String> tags = application.getTags();
		
		msg.put("apimgmt.application", application);
		msg.put("codeApp", codeAramis = toLowerCase(tags.get("codeAramis")));
		msg.put("idApp", toLowerCase(tags.get("idApplication")));
	}
	
	return codeAramis != null;
}

public static String toLowerCase(String value) {
	return value == null ? null : value.toLowerCase();
}

package icdc.oauth

import com.vordel.circuit.CircuitAbortException
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.authn.HTTP401Exception
import com.vordel.mime.HeaderSet

boolean invoke(Message msg) {
	CircuitAbortException exception = msg.get(MessageProperties.CIRCUIT_EXCEPTION);
	Throwable cause = exception == null ? null : exception.getCause();
	
	if (cause instanceof HTTP401Exception) {
		HeaderSet headers = msg.get(MessageProperties.HTTP_HEADERS);
		Iterator<String> names = headers.getHeaderNames();
		
		while(names.hasNext()) {
			String name = names.next();
			
			if ("Set-Cookie".equalsIgnoreCase(name)) {
				/* kerberos cookie, keep header */
			} else if ("WWW-Authenticate".equalsIgnoreCase(name)) {
				/* kerberos challenge, keep header */
			} else {
				names.remove();
			}
			
		}
	} else {
		msg.remove(MessageProperties.HTTP_HEADERS);
	}
	
	msg.remove(MessageProperties.CONTENT_BODY);
	
	return cause instanceof HTTP401Exception;
}

