package icdc.oauth

import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ObjectNode
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.ScriptHelper
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector

import groovy.transform.Field

@Field private static final Selector<String> REQUEST_JTI = SelectorResource.fromExpression("openid.request.jti", String.class);
@Field private static final ObjectMapper MAPPER = new ObjectMapper();

boolean invoke(Message msg) {
	/* remove existing headers and body */
	msg.remove(MessageProperties.HTTP_HEADERS);
	msg.remove(MessageProperties.CONTENT_BODY);
	
	String error = msg.get("oauth.error");

	if (error != null) {
		String error_uri = msg.get("oauth.error_uri");
		String error_description = msg.get("oauth.error_description");
		ObjectNode json = MAPPER.createObjectNode();
		String txn = REQUEST_JTI.substitute(msg);
		
		json.put("error", error);

		if (error_uri != null) {
			json.put("error_uri", error_uri);
		}

		if (error_description != null) {
			json.put("error_description", error_description);
		}
		
		if (txn != null) {
			json.put("txn", txn);
		}

		msg.put(MessageProperties.CONTENT_BODY, ScriptHelper.toJSONBody(json));
	}

	return error != null;
}

