package icdc.oauth

import java.security.MessageDigest
import java.security.PrivateKey
import java.security.PublicKey
import java.security.cert.X509Certificate
import java.security.interfaces.ECPublicKey
import java.security.interfaces.RSAPublicKey

import com.fasterxml.jackson.databind.JsonNode
import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ArrayNode
import com.fasterxml.jackson.databind.node.ObjectNode
import com.nimbusds.jose.JOSEObject
import com.nimbusds.jose.JWEDecrypter
import com.nimbusds.jose.JWEHeader
import com.nimbusds.jose.JWEObject
import com.nimbusds.jose.JWSHeader
import com.nimbusds.jose.JWSObject
import com.nimbusds.jose.JWSVerifier
import com.nimbusds.jose.crypto.ECDHDecrypter
import com.nimbusds.jose.crypto.RSADecrypter
import com.nimbusds.jose.crypto.factories.DefaultJWSVerifierFactory
import com.nimbusds.jose.proc.JWSVerifierFactory
import com.nimbusds.jose.util.Base64URL
import com.nimbusds.jwt.JWTClaimsSet
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.security.cert.PersonalInfo
import com.vordel.store.cert.CertStore
import com.vordel.trace.Trace

import groovy.transform.Field

/* CertStore x5t map, all certificates are accepted */
@Field private static final Map<String,PersonalInfo> X5T_MAP = buildX5Ts("SHA-1");
@Field private static final Map<String,PersonalInfo> X5T256_MAP = buildX5Ts("SHA-256");

@Field private static final JWSVerifierFactory VERIFIERS = new DefaultJWSVerifierFactory();
@Field private static final ObjectMapper MAPPER = new ObjectMapper();

@Field private static final Selector<URL> SERVICE_URL = SelectorResource.fromExpression(MessageProperties.HTTP_REQ_URL, URL.class);
@Field private static final Selector<String> REQUEST_JTI = SelectorResource.fromExpression("openid.request.jti", String.class);

@Field private static final Selector<String> ID_TOKEN_HINT = SelectorResource.fromExpression("oauth.form.id_token_hint", String.class);

boolean invoke(Message msg) {
	String id_token_hint = ID_TOKEN_HINT.substitute(msg);
	String jti = REQUEST_JTI.substitute(msg);
	String subject = null;

	boolean decrypted = false;
	boolean result = false;

	URL url = SERVICE_URL.substitute(msg);
	String self = null;
	
	if (url != null) {
		URI uri = url.toURI();
		
		uri = new URI(uri.getScheme(), uri.getAuthority(), uri.getPath(), null, null).normalize();
		
		msg.put("consent.request.uri", uri);
		self = uri.toString();
	}

	try {
		if (id_token_hint != null) {
			JOSEObject encrypted = JOSEObject.parse(id_token_hint);

			if (encrypted instanceof JWEObject) {
				JWEHeader header = encrypted.getHeader();
				PersonalInfo recipient = getX5TRecipient(header);

				if ((recipient != null) && (recipient.privateKey != null)) {
					/*
					 * XXX default decrypter factory does not work since private keys does not implement the right interface.
					 * Using public key to find the right decrypter.
					 */
					PublicKey publicKey = recipient.certificate.getPublicKey();
					JWEDecrypter decrypter = null;

					if (publicKey instanceof RSAPublicKey) {
						decrypter = new RSADecrypter((PrivateKey) recipient.privateKey);
					} else if (publicKey instanceof ECPublicKey) {
						/* XXX need to translate key before using it */
						decrypter = new ECDHDecrypter((PrivateKey) recipient.privateKey);
					} else {
						Trace.error("Only RSA and EC keys are supported");
					}

					if (decrypter != null) {
						encrypted.decrypt(decrypter);

						id_token_hint = encrypted.getPayload().toString();
						decrypted = true;

						Trace.info(String.format("Decrypted ID Token Hint : %s", id_token_hint));
					}
				}
			} else if (encrypted instanceof JWSObject) {
				decrypted = true;
			}
		}
	} catch(Exception any) {
		Trace.error("got error verifying signature", any);
	}

	try {
		if (decrypted) {
			/* parse incoming request */
			SignedJWT signed = SignedJWT.parse(id_token_hint);
			JWSHeader header = signed.getHeader();

			/* signer certificate from x5t claims */
			X509Certificate signer = getX5TSigner(header);

			if (signer != null) {
				JWSVerifier verifier = VERIFIERS.createJWSVerifier(header, signer.getPublicKey());

				if (signed.verify(verifier)) {
					JWTClaimsSet claims = signed.getJWTClaimsSet();
					
					String txn = claims.getStringClaim("txn");
					String sub = claims.getSubject();
					
					/* retrieve scopes descriptor */
					ObjectNode scopes = MAPPER.readTree(claims.getJSONObjectClaim("scopes").toJSONString());
					
					/* check token expiry, txn/sub/scopes claim presence, and that jti is equal to request jti */
					if (validateServiceClaims(msg, signer, claims, self) && (scopes != null) && (sub != null) && (txn != null) && (jti != null) && jti.equals(claims.getJWTID())) {
						Set<String> persistentGrant = appendScopes(new HashSet<String>(), scopes.get("permanent"));
						Set<String> transientGrant = appendScopes(new HashSet<String>(), scopes.get("transient"));
						
						Set<String> missing = new HashSet<String>();
						
						/* set message subject and txn */
						msg.put(MessageProperties.AUTHN_SUBJECT_ID, sub);
						msg.put("openid.id_token.txn", txn);

						/* recall existing grants */
						msg.put("scopes.allowed.persistent", persistentGrant);
						msg.put("scopes.allowed.transient", transientGrant);

						/* set message missing scopes (with compat for 'scopes.for.display') */
						msg.put("oauth.scopes.missing", appendScopes(missing, scopes.get("missing")));
						msg.put("scopes.for.display", MAPPER.writeValueAsString(scopes.get("missing")));
						
						/* do not forget to set signer (check that the certificate is still valid) */
						msg.put("openid.id_token.signer", signer);
						
						/* empty missing scopes is forbidden */
						result = !missing.isEmpty();
					}
				}
			}
		}
	} catch(Exception any) {
		Trace.error("got error verifying signature", any);
	}

	return result;
}

public static Set<String> appendScopes(Set<String> granted, ArrayNode scopes) {
	if (scopes != null) {
		for(JsonNode node : scopes) {
			String scope = node.asText(null);

			if (scope != null) {
				granted.add(scope);
			}
		}
	}

	return granted;
}

public static boolean validateServiceClaims(Message msg, X509Certificate signer, JWTClaimsSet claims, String self) {
	Date nbf = claims.getNotBeforeTime();
	Date exp = claims.getExpirationTime();
	Date now = new Date();

	boolean validated = false;

	/* compare dates */
	if ((nbf != null) && (exp != null) && nbf.before(now) && exp.after(now)) {
		List<String> aud = claims.getAudience();

		/* and check audience */
		validated = (aud != null) && (aud.contains(self));
	}
	return validated;
}

public static X509Certificate getX5TSigner(JWSHeader header) {
	Base64URL x5t = header.getX509CertSHA256Thumbprint();
	X509Certificate signer = null;

	if (x5t != null) {
		PersonalInfo entry = X5T256_MAP.get(x5t.toString());

		signer = entry == null ? null : entry.certificate;
	} else {
		x5t = header.getX509CertThumbprint();

		if (x5t != null) {
			PersonalInfo entry = X5T_MAP.get(x5t.toString());

			signer = entry == null ? null : entry.certificate;
		}
	}

	return signer;
}

public static PersonalInfo getX5TRecipient(JWEHeader header) {
	Base64URL x5t = header.getX509CertSHA256Thumbprint();
	PersonalInfo recipient = null;

	if (x5t != null) {
		recipient = X5T256_MAP.get(x5t.toString());
	} else {
		x5t = header.getX509CertThumbprint();

		if (x5t != null) {
			recipient = X5T_MAP.get(x5t.toString());
		}
	}

	return recipient;
}

public static Map<String,PersonalInfo> buildX5Ts(String algorithm) {
	Map<String,PersonalInfo> x5ts = new HashMap<String,PersonalInfo>();
	CertStore store = CertStore.getInstance();

	if (store != null) {
		Map<String, PersonalInfo> aliases = store.getAliasToInfo();
		MessageDigest hash = MessageDigest.getInstance(algorithm);

		for(PersonalInfo info : aliases.values()) {
			byte[] digest = hash.digest(info.certificate.getEncoded());
			String x5t = Base64URL.encode(digest).toString();
			PersonalInfo previous = x5ts.get(x5t);

			if ((previous == null) || ((previous.privateKey == null) && (info.privateKey != null)) ) {
				x5ts.put(x5t, info);
			}
		}
	}

	return Collections.unmodifiableMap(x5ts);
}

package icdc.oauth

import java.util.Map.Entry

import javax.ws.rs.core.MultivaluedMap
import javax.ws.rs.core.UriBuilder

import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.dwe.http.HTTPPlugin
import com.vordel.el.Selector
import com.vordel.mime.HeaderSet

import groovy.transform.Field

@Field private static final Selector<String> OAUTH_REQUEST = SelectorResource.fromExpression("oauth.form.request", String.class);
@Field private static final Selector<String> OAUTH_URI = SelectorResource.fromExpression("oauth.request.uri", String.class);
@Field private static final Selector<String> ID_TOKEN_HINT = SelectorResource.fromExpression("oauth.id_token.private", String.class);

boolean invoke(Message msg) {
	String signed = OAUTH_REQUEST.substitute(msg);
	String target = OAUTH_URI.substitute(msg);
	boolean result = false;

	if ((signed != null) && (target != null)) {
		HeaderSet headers = new HeaderSet();
		UriBuilder builder = UriBuilder.fromUri(new URI(target)).queryParam("request", signed);

		builder.queryParam("id_token_hint", ID_TOKEN_HINT.substitute(msg));

		/* set the new location header */
		headers.setHeader("Location", builder.build().toString());

		/* remove body and set redirect header */
		msg.remove(MessageProperties.CONTENT_BODY);
		msg.put(MessageProperties.HTTP_HEADERS, headers);

		/* set the 302 response */
		msg.put(MessageProperties.HTTP_RSP_STATUS, 302);
		msg.put(MessageProperties.HTTP_RSP_INFO, HTTPPlugin.getResponseText(302));

		result = true;
	}

	return result;
}

package icdc.oauth

import java.security.MessageDigest
import java.security.PrivateKey
import java.security.cert.X509Certificate

import com.fasterxml.jackson.databind.node.ObjectNode
import com.nimbusds.jose.JWSHeader
import com.nimbusds.jose.JWSVerifier
import com.nimbusds.jose.crypto.factories.DefaultJWSVerifierFactory
import com.nimbusds.jose.proc.JWSVerifierFactory
import com.nimbusds.jose.util.Base64URL
import com.nimbusds.jwt.JWTClaimsSet
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.ScriptHelper
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody
import com.vordel.security.cert.PersonalInfo
import com.vordel.store.cert.CertStore
import com.vordel.trace.Trace

import groovy.transform.Field

/* CertStore x5t map, all certificates are accepted */
@Field private static final Map<String,PersonalInfo> X5T_MAP = buildX5Ts("SHA-1");
@Field private static final Map<String,PersonalInfo> X5T256_MAP = buildX5Ts("SHA-256");

@Field private static final JWSVerifierFactory VERIFIERS = new DefaultJWSVerifierFactory();

@Field private static final Selector<String> OAUTH_REQUEST = SelectorResource.fromExpression("oauth.form.request", String.class);
@Field private static final Selector<URL> SERVICE_URL = SelectorResource.fromExpression(MessageProperties.HTTP_REQ_URL, URL.class);

boolean invoke(Message msg) {
	boolean result = false;

	try {
		URL url = SERVICE_URL.substitute(msg);
		String self = null;

		if (url != null) {
			URI uri = url.toURI();

			uri = new URI(uri.getScheme(), uri.getAuthority(), uri.getPath(), null, null).normalize();

			msg.put("login.request.uri", uri);
			self = uri.toString();
		}

		/* parse incoming request */
		SignedJWT signed = SignedJWT.parse(OAUTH_REQUEST.substitute(msg));
		JWSHeader header = signed.getHeader();

		/* signer certificate from x5t claims */
		X509Certificate signer = getX5TSigner(header);

		if (signer != null) {
			JWSVerifier verifier = VERIFIERS.createJWSVerifier(header, signer.getPublicKey());

			if (signed.verify(verifier)) {
				JWTClaimsSet claims = signed.getJWTClaimsSet();

				if (validateServiceClaims(msg, signer, claims, self)) {
					/* save the jti (will be used as nonce for private requests) */
					msg.put("openid.request.jti", claims.getJWTID());
					msg.put("openid.request.signer", signer);
					msg.put("oauth.request.uri", claims.getIssuer());

					result = true;
				}
			}
		}
	} catch(Exception any) {
		Trace.error("got error verifying signature", any);
	}

	return result;
}

public static boolean validateServiceClaims(Message msg, X509Certificate signer, JWTClaimsSet claims, String self) {
	List<String> aud = claims.getAudience();
	boolean validated = false;

	/* check that this object has been produced for us */
	if ((self != null) && (aud != null) && aud.contains(self)) {
		/* and compare dates */
		Date nbf = claims.getNotBeforeTime();
		Date exp = claims.getExpirationTime();
		Date now = new Date();

		/* check dates */
		if ((nbf != null) && (exp != null) && nbf.before(now) && exp.after(now)) {
			/* check that response_type and client_id are present */
			String response_type = claims.getStringClaim("response_type");
			String client_id = claims.getStringClaim("client_id");
			String iss = claims.getIssuer();

			/* check that we have at least 'response_type'  and 'client_id' */
			validated = (response_type != null) && (client_id != null)  && (iss != null) && aud.contains(iss);
		}
	}

	return validated;
}

public static X509Certificate getX5TSigner(JWSHeader header) {
	Base64URL x5t = header.getX509CertSHA256Thumbprint();
	X509Certificate signer = null;

	if (x5t != null) {
		PersonalInfo entry = X5T256_MAP.get(x5t.toString());

		signer = entry == null ? null : entry.certificate;
	} else {
		x5t = header.getX509CertThumbprint();

		if (x5t != null) {
			PersonalInfo entry = X5T_MAP.get(x5t.toString());

			signer = entry == null ? null : entry.certificate;
		}
	}

	return signer;
}

public static Map<String,PersonalInfo> buildX5Ts(String algorithm) {
	Map<String,PersonalInfo> x5ts = new HashMap<String,PersonalInfo>();
	CertStore store = CertStore.getInstance();

	if (store != null) {
		Map<String, PersonalInfo> aliases = store.getAliasToInfo();
		MessageDigest hash = MessageDigest.getInstance(algorithm);

		for(PersonalInfo info : aliases.values()) {
			byte[] digest = hash.digest(info.certificate.getEncoded());
			String x5t = Base64URL.encode(digest).toString();
			PersonalInfo previous = x5ts.get(x5t);

			if ((previous == null) || ((previous.privateKey == null) && (info.privateKey != null)) ) {
				x5ts.put(x5t, info);
			}
		}
	}

	return Collections.unmodifiableMap(x5ts);
}

package icdc.oauth

import java.nio.charset.Charset

import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.dwe.ByteArrayContentSource
import com.vordel.dwe.http.HTTPPlugin
import com.vordel.el.Selector
import com.vordel.mime.Body

import groovy.transform.Field

@Field private static final Selector<String> OAUTH_TEMPLATE = SelectorResource.fromExpression(MessageProperties.CONTENT_BODY, String.class);
@Field private static final Charset UTF8 = Charset.forName("UTF-8");

boolean invoke(Message msg) {
	String template = OAUTH_TEMPLATE.substitute(msg);
	Body body = msg.get(MessageProperties.CONTENT_BODY);

	template = SelectorResource.fromLiteral(template, String.class, false).substitute(msg);
	body = Body.create(body.getHeaders(), body.getContentType(), new ByteArrayContentSource(template.getBytes(UTF8)));

	/* remove http headers and set body */	
	msg.remove(MessageProperties.HTTP_HEADERS);
	msg.put(MessageProperties.CONTENT_BODY, body);

	/* set the success response */
	msg.put(MessageProperties.HTTP_RSP_STATUS, 200);
	msg.put(MessageProperties.HTTP_RSP_INFO, HTTPPlugin.getResponseText(200));

	return true;
}

package icdc.oauth

import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.dwe.http.HTTPPlugin
import com.vordel.el.Selector
import com.vordel.mime.HeaderSet
import com.vordel.trace.Trace

import groovy.transform.Field

@Field private static final Selector<String> REFERER = SelectorResource.fromExpression("http.headers['Referer']", String.class);

boolean invoke(Message msg) {
	String referer = REFERER.substitute(msg);
	boolean redirect = false;

	if (referer != null) {
		try {
			HeaderSet headers = new HeaderSet();
			URI uri = new URI(referer);

			uri = new URI(uri.getScheme(), uri.getAuthority(), uri.getPath(), uri.getQuery(), "error=loginfailed").normalize();

			/* set the new location header */
			headers.setHeader("Location", uri.toString());

			/* remove body and set redirect header */
			msg.remove(MessageProperties.CONTENT_BODY);
			msg.put(MessageProperties.HTTP_HEADERS, headers);

			/* set the 302 response */
			msg.put(MessageProperties.HTTP_RSP_STATUS, 302);
			msg.put(MessageProperties.HTTP_RSP_INFO, HTTPPlugin.getResponseText(302));
			redirect = true;
		} catch(Exception any) {
			Trace.error("got error parsing referer", any);
		}

	}

	return redirect;
}

