	if ((signed != null) && (target != null)) {
		HeaderSet headers = new HeaderSet();
		UriBuilder builder = UriBuilder.fromUri(new URI(target)).queryParam("request", signed);

		builder.queryParam("id_token_hint", ID_TOKEN_HINT.substitute(msg));

