package icdc.oauth

import com.fasterxml.jackson.databind.node.ArrayNode
import com.fasterxml.jackson.databind.node.ObjectNode
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody

import groovy.transform.Field

@Field private static final Selector<Set> ALLOWED_PERSISTENT = SelectorResource.fromExpression("scopes.allowed.persistent", Set.class);
@Field private static final Selector<Set> ALLOWED_TRANSIENT = SelectorResource.fromExpression("scopes.allowed.transient", Set.class);
@Field private static final Selector<Set> DISCARDED = SelectorResource.fromExpression("scopes.discarded", Set.class);

@Field private static final Selector<String> REQUEST_JTI = SelectorResource.fromExpression("openid.request.jti", String.class);

boolean invoke(Message msg) {
	Set<String> allowed_persistent = ALLOWED_PERSISTENT.substitute(msg);
	Set<String> allowed_transient = ALLOWED_TRANSIENT.substitute(msg);
	Set<String> discarded = DISCARDED.substitute(msg);
	ObjectNode scopes = null;

	if ((allowed_persistent != null) || (allowed_transient != null)) {
		ObjectNode body = ((JSONBody) msg.get(MessageProperties.CONTENT_BODY)).getJSON();

		scopes = body.putObject("scopes");

		if (allowed_persistent != null) {
			ArrayNode array = scopes.putArray("permanent");

			for(String scope : allowed_persistent) {
				array.add(scope);
			}
		}

		if (allowed_transient != null) {
			ArrayNode array = scopes.putArray("transient");

			for(String scope : allowed_transient) {
				array.add(scope);
			}
		}

		if (discarded != null) {
			ArrayNode array = scopes.putArray("discarded");

			for(String scope : discarded) {
				array.add(scope);
			}
		}

		scopes.put("txn", REQUEST_JTI.substitute(msg));
	}

	return scopes != null;
}

package icdc.oauth

import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.mime.QueryStringHeaderSet

boolean invoke(Message msg) {
	QueryStringHeaderSet query = msg.get(MessageProperties.HTTP_QUERYSTRING);
	boolean granted = false;

	/* get() may return a list or a string value */
	Object approval = query.get("approval");

	if ("allow".equals(approval)) {
		Iterator<String> grants = query.getHeaders("approvedScopes");

		Set<String> persistent = msg.get("scopes.allowed.persistent");
		Set<String> nonpersistent = msg.get("scopes.allowed.transient");
		Set<String> missing = msg.get("oauth.scopes.missing");
		Set<String> discarded = new HashSet<String>();

		if (persistent == null) {
			persistent = new HashSet<String>();
		}

		while((grants != null) && grants.hasNext()) {
			String scope = grants.next();

			if ((scope != null) && (!scope.isEmpty())) {
				persistent.add(scope);
			}
		}

		for(String scope : missing) {
			if ((scope != null) && (!persistent.contains(scope))) {
				discarded.add(scope);
			}
		}

		if (nonpersistent != null) {
			discarded.removeAll(nonpersistent);
		}

		msg.put("scopes.allowed.persistent", persistent);
		msg.put("scopes.discarded", discarded);
		granted = true;
	}

	return granted;
}

