importPackage(Packages.com.vordel.trace);


function invoke(msg)         {            

	var now, exp;
	var expirationTime=msg.get("expirationTime");
 	Trace.debug("expirationTime  : " + expirationTime);
	now = new Date();	
	now =  now.getTime() ;
	// Expiration en ms
	Trace.debug("now  : " + now);
	exp = now  + expirationTime * 1000;
	Trace.debug("exp1  : " + exp);

	//JWT : date exprimé en secondes et non en millisecondes
	exp = Math.round(exp/1000);
	Trace.debug("exp2  : " + exp);
	now = Math.round(now /1000);

	msg.put("now", now.toString());
	msg.put("expiration.time", exp.toString());

	return true;         
}

package icdc

import com.vordel.circuit.Message
import com.vordel.el.Selector
import com.vordel.trace.Trace

import groovy.transform.Field

@Field private static final Selector<Long> EXPIRATION_TIME = new Selector<Long>(String.format("\$%s", "{expirationTime}"), Long.class);

boolean invoke(Message msg) {
	Long expirationDelay = EXPIRATION_TIME.substitute(msg);
	
	if (expirationDelay != null) {
		long now = System.currentTimeMillis();
		long exp = now + (expirationDelay.longValue() * 1000L);
	
		Trace.debug("expirationTime  : " + expirationDelay);
		Trace.debug("now  : " + now);
		Trace.debug("exp1  : " + exp);
		
		exp /= 1000L;
		
		Trace.debug("exp2  : " + exp);
		
		msg.put("now", Long.valueOf(now).toString());
		msg.put("expiration.time", Long.valueOf(exp).toString());
	
		return true;
	} else {
		return false;
	}
}

import com.vordel.common.base64.Encoder;

def invoke(msg)         
{        
    String data = msg.get("jwt.payload");
    String base64EncodedData = Encoder.encodeNoLineBreaks(data.getBytes());
    msg.put("jwt.payload.b64", base64EncodedData);
    return true;        
}

