package icdc.oauth

import com.fasterxml.jackson.databind.JsonNode
import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ArrayNode
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.oauth.token.OAuth2AccessToken
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.trace.Trace

import groovy.transform.Field

@Field private static final Selector<URL> SERVICE_URL = SelectorResource.fromExpression(MessageProperties.HTTP_REQ_URL, URL.class);

@Field private static final Selector<OAuth2AccessToken> ACCESS_TOKEN = SelectorResource.fromExpression("accesstoken", OAuth2AccessToken.class);

@Field private static final ObjectMapper MAPPER = new ObjectMapper();

boolean invoke(Message msg) {
	OAuth2AccessToken accessToken = ACCESS_TOKEN.substitute(msg);
	Map<String, String> info = null;
	String id_token = null;

	try {
		URL url = SERVICE_URL.substitute(msg);

		if (url != null) {
			URI uri = url.toURI();

			uri = new URI(uri.getScheme(), uri.getAuthority(), uri.getPath(), null, null).normalize();

			msg.put("userinfo.request.uri", uri);
		}

		if (accessToken != null) {
			info = accessToken.getAdditionalInformation();
		}
	} catch(Exception any) {
		Trace.error("unexpected error", any);
	}

	if (info != null) {
		Set<String> additionalScopes = new LinkedHashSet<String>();

		if (info.containsKey("oauth.scopes.additional")) {
			try {
				ArrayNode additional = (ArrayNode) MAPPER.readTree(info.get("oauth.scopes.additional"));
				Iterator<JsonNode> iterator = additional.iterator();

				while(iterator.hasNext()) {
					String scope = iterator.next().asText(null);

					if (scope != null) {
						additionalScopes.add(scope);
					}
				}
			} catch (ClassCastException e) {
				Trace.error("unexpected error", e);
			} catch (IOException e) {
				Trace.error("unexpected error", e);
			}
		}
		/*
		 * Retrieve private ID Token
		 */
		id_token = info.get("oauth.id_token.private");

		/*
		 * Retrieve input for claims validation and generation
		 */
		msg.put("oauth.request.client_id", accessToken.getClientID());
		msg.put("oauth.id_token.claims", info.get("oauth.id_token.claims"));
		msg.put("oauth.id_token.public", info.get("id_token"));
		msg.put("oauth.id_token.private", id_token);

		/* set token scopes in message */
		msg.put("oauth.scopes.requested", accessToken.getScope());
		msg.put("oauth.scopes.additional", additionalScopes);

		/* select 'userinfo' for identity claims generation */
		msg.put("oauth.id_token.service", "userinfo");
	}

	return id_token != null;
}

package icdc.oauth

import com.fasterxml.jackson.databind.JsonNode
import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ObjectNode
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody

import groovy.transform.Field

@Field private static final Selector<String> ID_TOKEN = SelectorResource.fromExpression("oauth.id_token.public", String.class);
@Field private static final Selector<String> MESSAGE_ID = SelectorResource.fromExpression("id", String.class);

@Field private static final Set<String> RELAYED_CLAIMS = relayedClaims();
@Field private static final ObjectMapper MAPPER = new ObjectMapper();

boolean invoke(Message msg) {
	ObjectNode generated = ((JSONBody) msg.get(MessageProperties.CONTENT_BODY)).getJSON();
	ObjectNode id_token = parseJWT(ID_TOKEN.substitute(msg));

	for(String claim : RELAYED_CLAIMS) {
		generated.remove(claim);
	}

	if (id_token != null) {
		for(String claim : RELAYED_CLAIMS) {
			JsonNode node = id_token.get(claim);

			if ((node != null) && (!node.isNull())) {
				generated.set(claim, node);
			}
		}
	}

	/* Set iat as long otherwise, groovy set the expression to return BigDecimal */	
	long iat = System.currentTimeMillis() / 1000L;

	generated.put("iat", iat);
	generated.put("jti", MESSAGE_ID.substitute(msg));

	return true;
}

public static ObjectNode parseJWT(String id_token) {
	ObjectNode claims = null;

	if (id_token != null) {
		SignedJWT signed = SignedJWT.parse(id_token);

		claims = MAPPER.readTree(signed.getPayload().toString());
	}

	return claims;
}

public static Set<String> relayedClaims() {
	Set<String> claims = new HashSet<String>();

	claims.add("iss");
	claims.add("aud");
	claims.add("nonce");

	return Collections.unmodifiableSet(claims);
}

package icdc.oauth

import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.mime.HeaderSet

boolean invoke(Message msg) {
	HeaderSet headers = msg.get(MessageProperties.HTTP_HEADERS);

	if (headers != null) {
		Iterator<String> names = headers.getHeaderNames();

		while(names.hasNext()) {
			String name = names.next();

			if ("WWW-Authenticate".equalsIgnoreCase(name)) {
				/* authenticate challenge, keep header */
			} else {
				names.remove();
			}
		}
	}

	return true;
}

package icdc

import com.fasterxml.jackson.databind.JsonNode
import com.fasterxml.jackson.databind.ObjectMapper;
import com.vordel.circuit.Message
import com.vordel.kps.Store
import com.vordel.kps.Transaction
import com.vordel.kps.impl.KPS;
import com.vordel.mime.Body
import com.vordel.mime.JSONBody;
import com.vordel.trace.Trace;

import groovy.transform.Field

@Field private static final ObjectMapper MAPPER = new ObjectMapper();

boolean invoke(Message msg) {
	Body body = msg.get("content.body");
	String login = msg.get("authentication.subject.id");
	boolean status= true;

	if (body instanceof JSONBody) {
		Store store = KPS.getInstance().getStore("OAuthRefreshTokens"); // route -> KPS alias name
		Transaction transaction = store.beginTransaction();

		try {
			JsonNode node = body.getJSON();

			for (int i= 0; i < node.size() ; i++) {
				Map<String, Object> map = MAPPER.convertValue(node.get(i), Map.class);

				String refreshToken = map.get("refreshToken");
				String userAuthentication = map.get("userAuthentication");
				String tokenId = map.get("tokenId");

				Trace.debug(String.format("debug refreshToken tokenId  : %s", tokenId));
				
				try {
					transaction.delete(refreshToken);

					Trace.debug(String.format("delete refreshToken  : %s (%s)", refreshToken, login));
				} catch(e) {
					status &= false;
					
					Trace.error("Unexpected Exception : ", e);
				}
			}
		} finally {
			transaction.close();
		}

		return status;
	}

	return false;
}

import java.util.Map;
import com.vordel.trace.Trace;
import com.vordel.kps.impl.KPS;
import com.vordel.mime.JSONBody;
import com.fasterxml.jackson.databind.ObjectMapper;
import groovy.transform.Field;

@Field def mapper = new ObjectMapper();
def invoke(msg) 
{ 
def jsonBody = msg.get("content.body");
def login = msg.get("authentication.subject.id");
def status= true;
if(jsonBody instanceof JSONBody){
	def store = KPS.getInstance().getStore("OAuthRefreshTokens"); // route -> KPS alias name
	def transaction = store.beginTransaction();
	def jsonNode = jsonBody.getJSON();
	for  ( i= 0; i <jsonNode.size() ; i++){
		def map = mapper.convertValue(jsonNode.get(i), Map.class);
		def refreshToken = map.get("refreshToken");
		def userAuthentication = map.get("userAuthentication");
		def tokenId = map.get("tokenId");
		Trace.debug("debug refreshToken tokenId  : " + tokenId );
       		try{
				transaction.delete(tokenId);
				Trace.debug("delete refreshToken  : " + refreshToken + "( " +login +" )");
			}
			catch(e){
				status = false;
				Trace.error("erreur1:"+e);
			}
		
	}
	transaction.close();
	return status;
}
Trace.error("jsonBody:"+jsonBody );
return false;
}

