package icdc.oauth

import java.util.Map.Entry

import com.fasterxml.jackson.databind.JsonNode
import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ArrayNode
import com.fasterxml.jackson.databind.node.ObjectNode
import com.nimbusds.jwt.JWTClaimsSet
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector

import groovy.transform.Field
import net.minidev.json.JSONObject

@Field private static final Base64.Encoder ENCODER = Base64.getEncoder();
@Field private static final ObjectMapper MAPPER = new ObjectMapper();
@Field private static final Set<String> STD_CLAIMS = getSTDClaims();

@Field private static final Selector<String> ID_TOKEN = SelectorResource.fromExpression("oauth.id_token.private", String.class);

boolean invoke(Message msg) {
	SignedJWT signed = parseIDToken(ID_TOKEN.substitute(msg));
	boolean done = false;

	if (signed != null) {
		JWTClaimsSet claims = signed.getJWTClaimsSet();
		ObjectNode icdc = parseICDCClaims(claims.getJSONObjectClaim("icdc"));

		if (icdc == null) {
			icdc = MAPPER.createObjectNode();
		}

		/* copy standard claims to ICDC token */		
		relaySTDClaims(icdc, claims);

		String given_name = claims.getStringClaim("given_name");
		String family_name = claims.getStringClaim("family_name");
		String email = claims.getStringClaim("email");
		String phone_number = claims.getStringClaim("phone_number");

		if (family_name != null) {
			/* copy OpenID family name to icdc token */
			icdc.put("urn:cdc:icdc:claims:nom", family_name);
		} else {
			icdc.remove("urn:cdc:icdc:claims:nom")
		}

		if (given_name != null) {
			/* copy OpenID given name to icdc token */
			icdc.put("urn:cdc:icdc:claims:prenom", given_name);
		} else {
			icdc.remove("urn:cdc:icdc:claims:prenom")
		}

		if (email != null) {
			/* copy OpenID email to icdc token */
			icdc.put("urn:cdc:icdc:claims:courriel", email);
		} else {
			icdc.remove("urn:cdc:icdc:claims:courriel")
		}

		if (phone_number != null) {
			/* copy OpenID email to icdc token */
			icdc.put("urn:cdc:icdc:claims:telephone", phone_number);
		} else {
			icdc.remove("urn:cdc:icdc:claims:telephone")
		}

		/* encode the ICDC payload */
		String payload = MAPPER.writeValueAsString(icdc);
		String encoded = ENCODER.encodeToString(MAPPER.writeValueAsBytes(icdc));

		/* and set it in the message */
		msg.put("jwt.payload", payload);
		msg.put("jwt.payload.b64", encoded);

		done = true;
	}

	return done;
}

public static SignedJWT parseIDToken(String id_token) {
	return id_token == null ? null : SignedJWT.parse(id_token);
}

public static ObjectNode parseICDCClaims(JSONObject icdc) {
	return icdc == null ? null : MAPPER.readTree(icdc.toJSONString());
}

public static String toString(ArrayNode array, char separator) {
	if (array != null) {
		StringBuilder builder = new StringBuilder();
		Iterator<JsonNode> iterator = array.iterator();

		while(iterator.hasNext()) {
			if (iterator.next().isNull()) {
				iterator.remove();
			}
		}

		iterator = array.iterator();

		if (iterator.hasNext()) {
			builder.append(iterator.next().asText(null));

			while(iterator.hasNext()) {
				builder.append(separator);
				builder.append(iterator.next().asText(null));
			}

			return builder.toString();
		}
	}

	return null;
}

public static void relaySTDClaims(ObjectNode to, JWTClaimsSet claims) {
	ObjectNode from = MAPPER.readTree(claims.toString());
	Iterator<Entry<String, JsonNode>> entries = from.fields();

	while(entries.hasNext()) {
		Entry<String, JsonNode> entry = entries.next();
		String name = entry.getKey();

		if (STD_CLAIMS.contains(name)) {
			JsonNode node = entry.getValue();

			if ((node ==null) || node.isNull() || node.isMissingNode()) {
				to.remove(name);
			} else {
				to.set(name, node);
			}
		}
	}
}

public static Set<String> getSTDClaims() {
	Set<String> claims = new HashSet<String>();

	/* standard JWT claims */
	claims.add("sub");
	claims.add("exp");
	claims.add("nbf");

	/* standard OpenID claims */
	claims.add("updated_at");
	claims.add("azp");
	claims.add("nonce");
	claims.add("auth_time");

	return Collections.unmodifiableSet(claims);
}

package icdc.oauth

import java.security.MessageDigest
import java.security.PrivateKey
import java.security.PublicKey
import java.security.cert.X509Certificate
import java.security.interfaces.ECPublicKey
import java.security.interfaces.RSAPublicKey

import com.fasterxml.jackson.databind.JsonNode
import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ArrayNode
import com.fasterxml.jackson.databind.node.ObjectNode
import com.nimbusds.jose.JOSEObject
import com.nimbusds.jose.JWEDecrypter
import com.nimbusds.jose.JWEHeader
import com.nimbusds.jose.JWEObject
import com.nimbusds.jose.JWSHeader
import com.nimbusds.jose.JWSObject
import com.nimbusds.jose.JWSVerifier
import com.nimbusds.jose.crypto.ECDHDecrypter
import com.nimbusds.jose.crypto.RSADecrypter
import com.nimbusds.jose.crypto.factories.DefaultJWSVerifierFactory
import com.nimbusds.jose.proc.JWSVerifierFactory
import com.nimbusds.jose.util.Base64URL
import com.nimbusds.jwt.JWTClaimsSet
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.circuit.script.context.resources.KPSResource
import com.vordel.circuit.script.context.resources.KPSStoreResource
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.kps.ObjectNotFound
import com.vordel.security.cert.PersonalInfo
import com.vordel.store.cert.CertStore
import com.vordel.trace.Trace

import groovy.transform.Field

/* CertStore x5t map, all certificates are accepted */
@Field private static final Map<String,PersonalInfo> X5T_MAP = buildX5Ts("SHA-1");
@Field private static final Map<String,PersonalInfo> X5T256_MAP = buildX5Ts("SHA-256");

@Field private static final JWSVerifierFactory VERIFIERS = new DefaultJWSVerifierFactory();
@Field private static final ObjectMapper MAPPER = new ObjectMapper();


@Field private static final Selector<String> REQUEST_JTI = SelectorResource.fromExpression("openid.request.jti", String.class);

@Field private static final Selector<String> CLIENT_ID = SelectorResource.fromExpression("oauth.request.client_id", String.class);
@Field private static final Selector<String> ID_TOKEN_HINT = SelectorResource.fromExpression("oauth.request.parsed.mapped['id_token_hint']", String.class);
@Field private static final Selector<String> SERVICE_URI = SelectorResource.fromExpression("oauth.request.uri", String.class);

@Field private static final Selector<String> LOGIN_URI = SelectorResource.fromExpression("oauth.authenticate.uri", String.class);
@Field private static final Selector<String> CONSENT_URI = SelectorResource.fromExpression("oauth.consent.uri", String.class);

@Field private static final KPSResource AUTHENTICATION_TOKENS = getKPSResourceByAlias("OAuthAuthentications_V1");
@Field private static final KPSResource ID_TOKENS = getKPSResourceByAlias("OAuthIDTokens_V1");

boolean invoke(Message msg) {
	String id_token_hint = ID_TOKEN_HINT.substitute(msg);
	String subject = null;

	boolean decrypted = false;
	boolean result = false;

	/* ensure all token metadata remains private by providing an empty public set */
	msg.put("oauth.response.claims.public", new HashSet<String>());

	try {
		if (id_token_hint != null) {
			JOSEObject encrypted = JOSEObject.parse(id_token_hint);

			if (encrypted instanceof JWEObject) {
				JWEHeader header = encrypted.getHeader();
				PersonalInfo recipient = getX5TRecipient(header);

				if ((recipient != null) && (recipient.privateKey != null)) {
					/*
					 * XXX default decrypter factory does not work since private keys does not implement the right interface.
					 * Using public key to find the right decrypter.
					 */
					PublicKey publicKey = recipient.certificate.getPublicKey();
					JWEDecrypter decrypter = null;

					if (publicKey instanceof RSAPublicKey) {
						decrypter = new RSADecrypter((PrivateKey) recipient.privateKey);
					} else if (publicKey instanceof ECPublicKey) {
						/* XXX need to translate key before using it */
						decrypter = new ECDHDecrypter((PrivateKey) recipient.privateKey);
					} else {
						Trace.error("Only RSA and EC keys are supported");
					}

					if (decrypter != null) {
						encrypted.decrypt(decrypter);

						id_token_hint = encrypted.getPayload().toString();
						decrypted = true;

						Trace.info(String.format("Decrypted ID Token Hint : %s", id_token_hint));
					}
				}
			} else if (encrypted instanceof JWSObject) {
				decrypted = true;
			}
		}
	} catch(Exception any) {
		Trace.error("got error verifying signature", any);
	}

	try {
		if (decrypted) {
			/* parse incoming request */
			SignedJWT signed = SignedJWT.parse(id_token_hint);
			JWSHeader header = signed.getHeader();

			/* signer certificate from x5t claims */
			X509Certificate signer = getX5TSigner(header);

			if (signer != null) {
				JWSVerifier verifier = VERIFIERS.createJWSVerifier(header, signer.getPublicKey());

				if (signed.verify(verifier)) {
					JWTClaimsSet claims = signed.getJWTClaimsSet();

					if (!hasErrorClaims(msg, claims)) {
						String iss = claims.getIssuer();

						String login = LOGIN_URI.substitute(msg);
						String consent = CONSENT_URI.substitute(msg);
						String self = SERVICE_URI.substitute(msg);

						if ((iss != null) && (self != null)) {
							if (iss.equals(consent)) {
								/*
								 * If processing a consent id_token, retrieve private identity
								 * from the txn claim.
								 */
								String txn = claims.getStringClaim("txn");

								if ((txn != null) && validateServiceClaims(msg, signer, claims, self)) {
									try {
										/*
										 * Retrieve user full identity from KPS backend
										 */
										Map<String, Object> persisted = AUTHENTICATION_TOKENS.getCached(txn);
										String id_token = persisted.get("id_token");

										SignedJWT identity = SignedJWT.parse(id_token);

										/* read scopes from actual claims */
										ObjectNode scopes = MAPPER.readTree(claims.getJSONObjectClaim("scopes").toJSONString());

										txn = scopes.path("txn").asText(null);
										claims = identity.getJWTClaimsSet();

										/* ensure identity is still valid and nested txn is equal to the request txn */
										if (txn.equals(REQUEST_JTI.substitute(msg)) && validateServiceClaims(msg, signer, claims, self)) {
											Set<String> persistentGrant = appendScopes(new HashSet<String>(), scopes.get("permanent"));
											Set<String> transientGrant = appendScopes(new HashSet<String>(), scopes.get("transient"));
											Set<String> ignored = appendScopes(new HashSet<String>(), scopes.get("discarded"));

											persistentGrant.removeAll(ignored);
											transientGrant.removeAll(ignored);
											persistentGrant.removeAll(transientGrant);

											subject = claims.getSubject();

											msg.put("oauth.id_token.private", id_token);
											msg.put("scopes.allowed.persistent", persistentGrant);
											msg.put("scopes.allowed.transient", transientGrant);
											msg.put("scopes.discarded", ignored);

											result = true;
										}
									} catch(ObjectNotFound e) {
										/* the provided ID Token does not exists... exit */
										Trace.error("the provided ID Token 'txn' claim is not valid", e);
									}
								}
							} else if (iss.equals(login)) {
								/* never perform authentication redirect */
								result = true;

								/*
								 * If processing a login id_token, txn must be provided. if a new user has logged in
								 * the txn MUST be equal to the request jti claim. otherwise, the txp MUST be equal
								 * to an already persisted identity.
								 */
								if (validateServiceClaims(msg, signer, claims, self)) {
									String txn = claims.getStringClaim("txn");

									if (txn != null) {
										/* check that the claim 'txn' is equal to the request object 'jti' */
										if (txn.equals(REQUEST_JTI.substitute(msg))) {
											subject = claims.getSubject();

											if (subject != null) {
												Map<String, Object> persisted = new HashMap<String, Object>();
												Long ttl = claims.getExpirationTime().getTime() - System.currentTimeMillis();

												if ((ttl % 1000L) != 0) {
													/* round to next second if needed */
													ttl += 1000L;
												}

												if (ttl > 0L) {
													/* convert to seconds */
													ttl /= 1000L;

													persisted.put("txn", txn);
													persisted.put("id_token", id_token_hint);

													AUTHENTICATION_TOKENS.createEntry(persisted, ttl.intValue());
													msg.put("oauth.id_token.private", id_token_hint);
												}
											}
										} else {
											try {
												/*
												 * if the txn is not equal to the request 'jti', it must be already persisted and still valid.
												 */
												Map<String, Object> persisted = AUTHENTICATION_TOKENS.getCached(txn);
												String id_token = persisted.get("id_token");

												SignedJWT identity = SignedJWT.parse(id_token);

												/* 
												 * replace identity claims from the previous registered ones 
												 * do not replace signer since the persisted one has already been checked
												 */
												claims = identity.getJWTClaimsSet();

												if (validateServiceClaims(msg, signer, claims, self)) {
													/* set subject only if provided ID token is valid */
													subject = claims.getSubject();
													msg.put("oauth.id_token.private", id_token);
												}
											} catch(ObjectNotFound e) {
												/* the provided ID Token does not exists... exit */
												Trace.error("the provided ID Token 'txn' claim is not valid", e);
											}
										}
									}
								}
							} else {
								/*
								 * Processing an external id_token, retrieve private id token (if any).
								 * policy will return false to validate that private identity is still valid.
								 * The identity provider will be called with the existing private token.
								 */
								String jti = claims.getJWTID();
								String client_id = CLIENT_ID.substitute(msg);

								if ((jti != null) && (client_id != null) && validateServiceClaims(msg, signer, claims, self)) {
									List<String> aud = claims.getAudience();

									if (aud != null) {
										try {
											if (aud.contains(client_id)) {
												Map<String, Object> persisted = ID_TOKENS.getCached(jti);

												/* ensure the given token has been generated by us */
												if (id_token_hint.equals(persisted.get("id_token"))) {
													String txn = persisted.get("txn");

													/* replace public ID Token by private copy (if any) */
													persisted = AUTHENTICATION_TOKENS.getCached(txn);
													msg.put("oauth.id_token.private", persisted.get("id_token"));
												}
											} else {
												Trace.error("a client tried to use an ID Token which has not be delivered to him")
											}
										} catch(ObjectNotFound e) {
											/* the provided ID Token does not exists... exit */
											Trace.error("the provided ID Token is not valid", e);
										}
									}
								}
							}

							if (subject == null) {
								msg.remove("authentication.subject.id");
							} else {
								/* qualify username with the login service URI (make username unique for consent storage)*/
								URI qualified = new URI(login);

								qualified = new URI(qualified.getScheme(), subject, qualified.getHost(), qualified.getPort(), qualified.getPath(), qualified.getQuery(), qualified.getFragment());
								msg.put("authentication.subject.id", qualified.toString());
							}

							msg.put("id_token.signer", signer);
						}
					}
				}
			}
		}
	} catch(Exception any) {
		Trace.error("got error verifying signature", any);
	}

	return result;
}

public static Set<String> appendScopes(Set<String> granted, ArrayNode scopes) {
	if (scopes != null) {
		for(JsonNode node : scopes) {
			String scope = node.asText(null);

			if (scope != null) {
				granted.add(scope);
			}
		}
	}

	return granted;
}

public static KPSResource getKPSResourceByAlias(String alias) {
	/* XXX fix policy studio compilation */
	return CertStore.getInstance() == null ? null : new KPSStoreResource(alias);
}

public static boolean hasErrorClaims(Message msg, JWTClaimsSet claims) {
	String error = claims.getStringClaim("error");
	String error_uri = claims.getStringClaim("error_uri");
	String error_description = claims.getStringClaim("error_description");

	if (error_uri != null) {
		if (error == null) {
			error = "server_error";
		}

		msg.put("oauth.error_uri", error_uri);
	} else {
		msg.remove("oauth.error_uri");
	}

	if (error_description != null) {
		if (error == null) {
			error = "server_error";
		}

		msg.put("oauth.error_description", error_description);
	} else {
		msg.remove("oauth.error_description");
	}

	if (error != null) {
		msg.put("oauth.error", error);
	} else {
		msg.remove("oauth.error");
	}

	return (error != null);
}

public static boolean validateServiceClaims(Message msg, X509Certificate signer, JWTClaimsSet claims, String self) {
	Date nbf = claims.getNotBeforeTime();
	Date exp = claims.getExpirationTime();
	Date now = new Date();

	boolean validated = false;

	/* compare dates */
	if ((nbf != null) && (exp != null) && nbf.before(now) && exp.after(now)) {
		List<String> aud = claims.getAudience();

		/* and check audience */
		validated = (aud != null) && (aud.contains(self));
	}
	return validated;
}

public static X509Certificate getX5TSigner(JWSHeader header) {
	Base64URL x5t = header.getX509CertSHA256Thumbprint();
	X509Certificate signer = null;

	if (x5t != null) {
		PersonalInfo entry = X5T256_MAP.get(x5t.toString());

		signer = entry == null ? null : entry.certificate;
	} else {
		x5t = header.getX509CertThumbprint();

		if (x5t != null) {
			PersonalInfo entry = X5T_MAP.get(x5t.toString());

			signer = entry == null ? null : entry.certificate;
		}
	}

	return signer;
}

public static PersonalInfo getX5TRecipient(JWEHeader header) {
	Base64URL x5t = header.getX509CertSHA256Thumbprint();
	PersonalInfo recipient = null;

	if (x5t != null) {
		recipient = X5T256_MAP.get(x5t.toString());
	} else {
		x5t = header.getX509CertThumbprint();

		if (x5t != null) {
			recipient = X5T_MAP.get(x5t.toString());
		}
	}

	return recipient;
}

public static Map<String,PersonalInfo> buildX5Ts(String algorithm) {
	Map<String,PersonalInfo> x5ts = new HashMap<String,PersonalInfo>();
	CertStore store = CertStore.getInstance();

	if (store != null) {
		Map<String, PersonalInfo> aliases = store.getAliasToInfo();
		MessageDigest hash = MessageDigest.getInstance(algorithm);

		for(PersonalInfo info : aliases.values()) {
			byte[] digest = hash.digest(info.certificate.getEncoded());
			String x5t = Base64URL.encode(digest).toString();
			PersonalInfo previous = x5ts.get(x5t);

			if ((previous == null) || ((previous.privateKey == null) && (info.privateKey != null)) ) {
				x5ts.put(x5t, info);
			}
		}
	}

	return Collections.unmodifiableMap(x5ts);
}

package icdc.oauth

import java.util.Map.Entry

import javax.ws.rs.core.MultivaluedMap
import javax.ws.rs.core.UriBuilder

import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.dwe.http.HTTPPlugin
import com.vordel.el.Selector
import com.vordel.mime.HeaderSet

import groovy.transform.Field

@Field private static final Selector<String> OAUTH_REQUEST = SelectorResource.fromExpression("oauth.redirect.request", String.class);
@Field private static final Selector<String> LOGIN_URI = SelectorResource.fromExpression("oauth.authenticate.uri", String.class);
@Field private static final Selector<String> ID_TOKEN_HINT = SelectorResource.fromExpression("oauth.id_token.private", String.class);

@Field private static final Selector<MultivaluedMap> EXTRA_PARAMETERS = SelectorResource.fromExpression("oauth.request.extras.form", MultivaluedMap.class);

boolean invoke(Message msg) {
	MultivaluedMap<?,?> extras = EXTRA_PARAMETERS.substitute(msg)
	String id_token_hint = ID_TOKEN_HINT.substitute(msg);
	String signed = OAUTH_REQUEST.substitute(msg);
	String target = LOGIN_URI.substitute(msg);
	boolean result = false;
	
	if ((signed != null) && (target != null)) {
		HeaderSet headers = new HeaderSet();
		UriBuilder builder = UriBuilder.fromUri(new URI(target)).queryParam("request", signed);
		
		if (id_token_hint != null) {
			builder.queryParam("id_token_hint", id_token_hint);
		}
		
		if (extras != null) {
			for(Entry<?, List<?>> entry : extras.entrySet()) {
				String key = String.valueOf(entry.getKey());
				
				for(Object value : entry.getValue()) {
					if (value != null) {
						builder.queryParam(key, String.valueOf(value));
					}
				}
			}
		}
		
		/* set the new location header */
		headers.setHeader("Location", builder.build().toString());

		/* remove body and set redirect header */
		msg.remove(MessageProperties.CONTENT_BODY);
		msg.put(MessageProperties.HTTP_HEADERS, headers);

		/* set the 302 response */
		msg.put(MessageProperties.HTTP_RSP_STATUS, 302);
		msg.put(MessageProperties.HTTP_RSP_INFO, HTTPPlugin.getResponseText(302));
		
		result = true;
	}

	return result;
}

package icdc.oauth

import com.vordel.circuit.Message
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.trace.Trace

import groovy.transform.Field

@Field private static final Selector<String> LOGIN_URI = SelectorResource.fromExpression("oauth.authenticate.uri", String.class);
@Field private static final Selector<String> CONSENT_URI = SelectorResource.fromExpression("oauth.consent.uri", String.class);
@Field private static final Selector<String> SERVICE_URI = SelectorResource.fromExpression("oauth.request.uri", String.class);

boolean invoke(Message msg) {
	try {
		String login_uri = normalize(msg, LOGIN_URI.substitute(msg));
		String consent_uri = normalize(msg, CONSENT_URI.substitute(msg));

		if (login_uri != null) {
			msg.put("oauth.authenticate.uri", login_uri);
		}

		if (consent_uri != null) {
			msg.put("oauth.consent.uri", consent_uri);
		}

		return true;
	} catch(Exception e) {
		Trace.error("unable to compute provider URI", e);

		return false;
	}
}

public static String normalize(Message msg, String provider_uri) {
	URI target = null;

	if (provider_uri != null) {
		target = new URI(provider_uri);

		if (target.getPath().startsWith("/")) {
			if (target.getAuthority() == null) {
				/* If Authority is missing, get the current service authority */
				URI authority = new URI(SERVICE_URI.substitute(msg));

				target = new URI(authority.getScheme(), authority.getAuthority(), target.getPath(), target.getQuery(), target.getFragment());
			}
		} else {
			/* if we have a relative path, resolve against the authority URI */
			URI authority = new URI(SERVICE_URI.substitute(msg));

			target = authority.resolve(target);
		}

		target = target.normalize();
	}

	return target == null ? null : target.toString();
}

package icdc.oauth

import com.fasterxml.jackson.databind.node.ObjectNode
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.ScriptHelper
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody

import groovy.transform.Field

@Field private static final Selector<ObjectNode> OAUTH_PARAMETERS = SelectorResource.fromExpression("oauth.request.parsed.json", ObjectNode.class);

boolean invoke(Message msg) {
	/* retrieve mapped parameters and id_token_hint */
	ObjectNode request = OAUTH_PARAMETERS.substitute(msg).deepCopy();

	/* remove id_token_hint from parameters */
	request.remove("id_token_hint");

	/* set serialized request as JSON Body */
	msg.put(MessageProperties.CONTENT_BODY, ScriptHelper.toJSONBody(request));

	return true;
}

package icdc.oauth

import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ObjectNode
import com.vordel.circuit.Message
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector

import groovy.transform.Field

@Field private static final Selector<Set> REQUESTED_SCOPES = SelectorResource.fromExpression("oauth.scopes.requested", Set.class);
@Field private static final Selector<Set> ADDITIONAL_SCOPES = SelectorResource.fromExpression("oauth.scopes.additional", Set.class);
@Field private static final Selector<ObjectNode> REQUESTED_CLAIMS = SelectorResource.fromExpression("oauth.request.parsed.mapped['claims']", ObjectNode.class);
@Field private static final Selector<String> REQUESTED_NONCE = SelectorResource.fromExpression("oauth.request.parsed.mapped['nonce']", String.class);

@Field private static final Set<String> PROFILE_CLAIMS = asSet("name", "family_name", "given_name", "middle_name", "nickname", "preferred_username", "profile", "picture", "website", "gender", "birthdate", "zoneinfo", "locale", "updated_at");
@Field private static final Set<String> EMAIL_CLAIMS = asSet("email", "email_verified");
@Field private static final Set<String> ADDRESS_CLAIMS = asSet("address");
@Field private static final Set<String> PHONE_CLAIMS = asSet("phone_number", "phone_number_verified");

/* France Connect additions */
@Field private static final Set<String> BIRTH_CLAIMS = asSet("birthplace", "birthcountry");

@Field private static final ObjectMapper MAPPER = new ObjectMapper();

boolean invoke(Message msg) {
	ObjectNode claims = REQUESTED_CLAIMS.substitute(msg);

	if (claims != null) {
		/* retrieve scopes which must have user consent without being in access token */
		Set<String> additional = ADDITIONAL_SCOPES.substitute(msg);

		if (additional == null) {
			return false;
		}

		Set<String> allowed = new HashSet<String>();

		/* compute all alowed claims */
		processScopeSet(REQUESTED_SCOPES.substitute(msg), allowed);
		processScopeSet(additional, allowed);

		/* update additional scope list according to claims request */
		processService(allowed, additional, claims.get("id_token"));
		processService(allowed, additional, claims.get("userinfo"));

		msg.put("oauth.id_token.claims", MAPPER.writeValueAsString(claims));

		String nonce = REQUESTED_NONCE.substitute(msg);

		if (nonce != null) {
			msg.put("oauth.id_token.nonce", nonce);
		}
	}

	return true;
}

public static void processScopeSet(Set<String> requested, Set<String> allowed) {
	if (requested != null) {
		if (requested.contains("profile")) {
			allowed.addAll(PROFILE_CLAIMS);
		}

		if (requested.contains("email")) {
			allowed.addAll(EMAIL_CLAIMS);
		}

		if (requested.contains("address")) {
			allowed.addAll(ADDRESS_CLAIMS);
		}

		if (requested.contains("phone")) {
			allowed.addAll(PHONE_CLAIMS);
		}

		if (requested.contains("birth")) {
			allowed.addAll(BIRTH_CLAIMS);
		}

		if (requested.contains("identite_pivot")) {
			allowed.addAll(PROFILE_CLAIMS);
			allowed.addAll(BIRTH_CLAIMS);
		}
	}
}

public static void processService(Set<String> allowed, Set<String> additional, ObjectNode service) {
	if (service != null) {
		Iterator<String> fields = service.fieldNames();

		while(fields.hasNext()) {
			String field = fields.next();

			if (!allowed.contains(field)) {
				if (PROFILE_CLAIMS.contains(field)) {
					additional.add("profile");
					allowed.addAll(PROFILE_CLAIMS);
				} else if (EMAIL_CLAIMS.contains(field)) {
					additional.add("email");
					allowed.addAll(EMAIL_CLAIMS);
				} else if (ADDRESS_CLAIMS.contains(field)) {
					additional.add("address");
					allowed.addAll(ADDRESS_CLAIMS);
				} else if (PHONE_CLAIMS.contains(field)) {
					additional.add("phone");
					allowed.addAll(PHONE_CLAIMS);
				} else if (BIRTH_CLAIMS.contains(field)) {
					additional.add("birth");
					allowed.addAll(BIRTH_CLAIMS);
				}
			}
		}
	}
}

public static Set<String> asSet(String... values) {
	Set<String> set = new HashSet<String>();

	for(String value : values) {
		if (value != null) {
			set.add(value);
		}
	}

	return Collections.unmodifiableSet(set);
}

var EXCEPTION_SELECTOR = com.vordel.circuit.script.context.resources.SelectorResource.fromExpression("extensions['oauth.helper'].OAuthException", javax.ws.rs.WebApplicationException.class);

function invoke(msg) {
	/* retrieve client_id from message*/
	var exception = EXCEPTION_SELECTOR.substitute(msg);

	if (exception != null) {
		msg.put("oauth.exception", exception);
	}

	return exception == null;
}

package icdc.oauth

import java.util.Map.Entry

import javax.ws.rs.WebApplicationException

import com.fasterxml.jackson.databind.JsonNode
import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ArrayNode
import com.fasterxml.jackson.databind.node.BooleanNode
import com.fasterxml.jackson.databind.node.MissingNode
import com.fasterxml.jackson.databind.node.ObjectNode
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector

import groovy.transform.Field

@Field private static final Selector<WebApplicationException> VALIDATION_ERROR = SelectorResource.fromExpression("extensions['oauth.helper'].OAuthException", WebApplicationException.class);
@Field private static final Selector<String> REQUESTED_OUTPUT = SelectorResource.fromExpression("oauth.id_token.service", String.class);

@Field private static final Selector<Set> REQUESTED_SCOPES = SelectorResource.fromExpression("oauth.scopes.requested", Set.class);
@Field private static final Selector<Set> ADDITIONAL_SCOPES = SelectorResource.fromExpression("oauth.scopes.additional", Set.class);
@Field private static final Selector<String> REQUESTED_CLAIMS = SelectorResource.fromExpression("oauth.id_token.claims", String.class);

@Field private static final Selector<String> ID_TOKEN = SelectorResource.fromExpression("oauth.id_token.private", String.class);
@Field private static final Selector<String> CLIENT_ID = SelectorResource.fromExpression("oauth.request.client_id", String.class);

@Field private static final ObjectMapper MAPPER = new ObjectMapper();

@Field private static final Set<String> PROFILE_CLAIMS = asSet("name", "family_name", "given_name", "middle_name", "nickname", "preferred_username", "profile", "picture", "website", "gender", "birthdate", "zoneinfo", "locale", "updated_at");
@Field private static final Set<String> EMAIL_CLAIMS = asSet("email", "email_verified");
@Field private static final Set<String> ADDRESS_CLAIMS = asSet("address");
@Field private static final Set<String> PHONE_CLAIMS = asSet("phone_number", "phone_number_verified");

/* France Connect additions */
@Field private static final Set<String> BIRTH_CLAIMS = asSet("birthplace", "birthcountry");

@Field private static final Set<String> RELAYED_CLAIMS = relayedClaims();

boolean invoke(Message msg) {
	Map<String, ClaimRequest> requestedClaims = new LinkedHashMap<String, ClaimRequest>();

	ObjectNode claims = parseClaims(REQUESTED_CLAIMS.substitute(msg));
	ObjectNode id_token = parseIdentity(ID_TOKEN.substitute(msg));
	ObjectNode generated = MAPPER.createObjectNode();

	Set<String> requestedScopes = REQUESTED_SCOPES.substitute(msg);

	for(String scope : requestedScopes) {
		if ("profile".equals(scope)) {
			addScopeRequestedClaims(requestedClaims, PROFILE_CLAIMS);
		} else if ("email".equals(scope)) {
			addScopeRequestedClaims(requestedClaims, EMAIL_CLAIMS);
		} else if ("address".equals(scope)) {
			addScopeRequestedClaims(requestedClaims, ADDRESS_CLAIMS);
		} else if ("phone".equals(scope)) {
			addScopeRequestedClaims(requestedClaims, PHONE_CLAIMS);
		} else if ("birth".equals(scope)) {
			addScopeRequestedClaims(requestedClaims, BIRTH_CLAIMS);
		} else if ("identite_pivot".equals(scope)) {
			addScopeRequestedClaims(requestedClaims, PROFILE_CLAIMS);
			addScopeRequestedClaims(requestedClaims, BIRTH_CLAIMS);
		}
	}

	Set<String> allowed = new HashSet<String>();

	/* compute all alowed claims */
	processScopeSet(requestedScopes, allowed);
	processScopeSet(ADDITIONAL_SCOPES.substitute(msg), allowed);

	if (claims != null) {
		String service = REQUESTED_OUTPUT.substitute(msg);

		try {
			processServiceRequest(requestedClaims, allowed, id_token, claims, "userinfo", "userinfo".equals(service));
			processServiceRequest(requestedClaims, allowed, id_token, claims, "id_token", "id_token".equals(service));
		} catch(IllegalArgumentException e) {
			msg.put("oauth.error", "invalid_request");
			msg.put("oauth.error_description", e.getMessage());

			msg.put("oauth.exception", VALIDATION_ERROR.substitute(msg));

			return false;
		}
	}

	Iterator<Entry<String, JsonNode>> entries = id_token.fields();

	while(entries.hasNext()) {
		Entry<String, JsonNode> entry = entries.next();
		String claim = entry.getKey();

		if (RELAYED_CLAIMS.contains(claim) || requestedClaims.containsKey(claim)) {
			generated.set(claim, entry.getValue());
		}
	}

	String client_id = CLIENT_ID.substitute(msg);

	if (client_id != null) {
		generated.put("azp", client_id);
	}

	msg.put("oauth.id_token.generated", MAPPER.writeValueAsString(generated));

	return true;
}

public static Set<String> relayedClaims() {
	Set<String> claims = new HashSet<String>();

	claims.add("nbf");
	claims.add("exp");
	claims.add("sub");
	claims.add("auth_time");
	claims.add("nonce");
	claims.add("acr");
	claims.add("amr");

	return Collections.unmodifiableSet(claims);
}

public static void processServiceRequest(Map<String, ClaimRequest> requestedClaims, Set<String> allowed, ObjectNode id_token, ObjectNode claims, String service, boolean generate) {
	ObjectNode request = claims.get(service);

	if (request != null) {
		Iterator<Entry<String, JsonNode>> entries = request.fields();

		while(entries.hasNext()) {
			Entry<String, JsonNode> entry = entries.next();
			String claim = entry.getKey();

			if (allowed.contains(claim)) {
				JsonNode parameters = entry.getValue();
				ClaimRequest parsed = null;

				if ((parameters == null) || parameters.isNull()) {
					parsed = new ClaimRequest();
				} else if (parameters instanceof ObjectNode) {
					parsed = new ClaimRequest(service, claim, parameters);
				} else {
					throw new IllegalArgumentException(String.format("invalid claims syntax for key %s in service %s", claim, service));
				}

				ArrayNode values = parsed.getValues();

				if ((values != null) && (!values.isEmpty())) {
					JsonNode existing = id_token.path(claim);

					if ((!(existing instanceof MissingNode)) && (!contains(values, existing))) {
						throw new IllegalArgumentException(String.format("claims value mismatch for key %s in service %s", claim, service));
					}
				}

				if (generate) {
					requestedClaims.put(claim, parsed);
				}
			}
		}
	}
}

private static boolean contains(ArrayNode values, JsonNode existing) {
	Iterator<JsonNode> iterator = values.iterator();
	boolean found = false;

	while((!found) && iterator.hasNext()) {
		found |= iterator.next().equals(existing);
	}

	return found;
}

public static void addScopeRequestedClaims(Map<String, ClaimRequest> requestedClaims, Set<String> claims) {
	for(String claim : claims) {
		requestedClaims.put(claim, new ClaimRequest());
	}
}

public static ObjectNode parseIdentity(String id_token) {
	ObjectNode claims = null;

	if (id_token != null) {
		SignedJWT signed = SignedJWT.parse(id_token);

		claims = MAPPER.readTree(signed.getPayload().toString());
	}

	return claims;
}

public static ObjectNode parseClaims(String serialized_claims) {
	ObjectNode claims = null;

	if (serialized_claims != null) {
		claims = MAPPER.readTree(serialized_claims);
	}

	return claims;
}

public static void processScopeSet(Set<String> requested, Set<String> allowed) {
	if (requested != null) {
		if (requested.contains("profile")) {
			allowed.addAll(PROFILE_CLAIMS);
		}

		if (requested.contains("email")) {
			allowed.addAll(EMAIL_CLAIMS);
		}

		if (requested.contains("address")) {
			allowed.addAll(ADDRESS_CLAIMS);
		}

		if (requested.contains("phone")) {
			allowed.addAll(PHONE_CLAIMS);
		}

		if (requested.contains("birth")) {
			allowed.addAll(BIRTH_CLAIMS);
		}

		if (requested.contains("identite_pivot")) {
			allowed.addAll(PROFILE_CLAIMS);
			allowed.addAll(BIRTH_CLAIMS);
		}
	}
}

public static Set<String> asSet(String... values) {
	Set<String> set = new HashSet<String>();

	for(String value : values) {
		if (value != null) {
			set.add(value);
		}
	}

	return Collections.unmodifiableSet(set);
}

class ClaimRequest {
	private static final ObjectMapper MAPPER = new ObjectMapper();

	private final boolean essential;
	private final ArrayNode values;

	public ClaimRequest() {
		this(false, null);
	}

	public ClaimRequest(String service, String claim, ObjectNode request) {
		JsonNode essential = request.path("essential");
		JsonNode values = request.path("values");
		JsonNode value = request.path("value");

		if (essential instanceof MissingNode) {
			essential = BooleanNode.FALSE;
		}

		if (!(essential instanceof BooleanNode)) {
			throw new IllegalArgumentException(String.format("essential must be null, true or false for key %s in service %s", claim, service));
		}

		if (value instanceof MissingNode) {
			if (values instanceof MissingNode) {
				values = null;
			} else if (!(values instanceof ArrayNode)) {
				throw new IllegalArgumentException(String.format("values must be an array node for key %s in service %s", claim, service));
			}
		} else {
			if (!(values instanceof MissingNode)) {
				throw new IllegalArgumentException(String.format("value and values are mutual exclusive for key %s in service %s", claim, service));
			}

			values = MAPPER.createArrayNode();

			((ArrayNode) values).add(value);
		}

		this.essential = ((BooleanNode) essential).asBoolean();
		this.values = values;
	}

	public ClaimRequest(JsonNode essential, JsonNode value) {
		ArrayNode values = null;

		if (value instanceof ArrayNode) {
			values = value;
		} else {
			values = MAPPER.createArrayNode();

			values.add(value);
		}

		this.essential = essential
		this.values = values;
	}

	public ClaimRequest(boolean essential, ArrayNode values) {
		this.essential = essential;
		this.values = values;
	}

	public boolean isEssential() {
		return essential;
	}

	public ArrayNode getValues() {
		return values;
	}
}

package icdc.oauth

import java.security.MessageDigest
import java.security.PublicKey
import java.security.cert.X509Certificate
import java.security.interfaces.ECPublicKey
import java.security.interfaces.RSAPublicKey

import com.nimbusds.jose.CompressionAlgorithm
import com.nimbusds.jose.EncryptionMethod
import com.nimbusds.jose.JWEAlgorithm
import com.nimbusds.jose.JWEEncrypter
import com.nimbusds.jose.JWEHeader
import com.nimbusds.jose.JWEObject
import com.nimbusds.jose.Payload
import com.nimbusds.jose.crypto.ECDHEncrypter
import com.nimbusds.jose.crypto.RSAEncrypter
import com.nimbusds.jose.util.Base64URL
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.trace.Trace

import groovy.transform.Field

@Field private static final Selector<String> ID_TOKEN_HINT = SelectorResource.fromExpression("oauth.id_token.private", String.class);
@Field private static final Selector<X509Certificate> OAUTH_RECIPIENT = SelectorResource.fromExpression("oauth.id_token.recipient", X509Certificate.class);

boolean invoke(Message msg) {
	String signed = ID_TOKEN_HINT.substitute(msg);
	X509Certificate recipient = OAUTH_RECIPIENT.substitute(msg);
	boolean result = false;

	if ((signed != null) && (recipient != null)) {
		PublicKey publicKey = recipient.getPublicKey();

		EncryptionMethod method = EncryptionMethod.A128CBC_HS256;
		JWEAlgorithm algorithm = null;
		JWEEncrypter encrypter = null;

		if (publicKey instanceof RSAPublicKey) {
			algorithm = JWEAlgorithm.RSA_OAEP_256;
			encrypter = new RSAEncrypter((RSAPublicKey) publicKey);
		} else if (publicKey instanceof ECPublicKey) {
			algorithm = JWEAlgorithm.ECDH_ES;
			encrypter = new ECDHEncrypter((ECPublicKey) publicKey);
		} else {
			Trace.error("Only RSA and EC keys are supported");
		}

		if (algorithm != null) {
			JWEHeader.Builder builder = new JWEHeader.Builder(algorithm, method);
			Payload payload = new Payload(SignedJWT.parse(signed));

			builder = builder.x509CertThumbprint(getX5T(recipient, "SHA-1"));
			builder = builder.x509CertSHA256Thumbprint(getX5T(recipient, "SHA-256"));
			builder = builder.compressionAlgorithm(CompressionAlgorithm.DEF);

			JWEObject encrypted = new JWEObject(builder.build(), new Payload(SignedJWT.parse(signed)));

			encrypted.encrypt(encrypter);
			msg.put("oauth.id_token.private", encrypted.serialize());
			result = true;
		}
	}

	return result;
}

public static Base64URL getX5T(X509Certificate recipient, String algorithm) {
	MessageDigest hash = MessageDigest.getInstance(algorithm);
	byte[] digest = hash.digest(recipient.getEncoded());

	return Base64URL.encode(digest);
}

package icdc.oauth

import com.vordel.circuit.Message
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector

import groovy.transform.Field

@Field private static final Selector<Boolean> DISABLED = SelectorResource.fromExpression("oauth.consent.disable", Boolean.class);

@Field private static final Selector<Set> REQUESTED = SelectorResource.fromExpression("oauth.scopes.requested", Set.class);
@Field private static final Selector<Set> MISSING = SelectorResource.fromExpression("oauth.scopes.missing", Set.class);

@Field private static final Selector<Set> PERMANENT_GRANT = SelectorResource.fromExpression("scopes.allowed.persistent", Set.class);
@Field private static final Selector<Set> TRANSIENT_GRANT = SelectorResource.fromExpression("scopes.allowed.transient", Set.class);
@Field private static final Selector<Set> DISCARDED = SelectorResource.fromExpression("scopes.discarded", Set.class);

boolean invoke(Message msg) {
	Boolean result = DISABLED.substitute(msg);

	if (result == null) {
		/* consent is enabled by default */
		result = false;
	} else if (result.booleanValue()) {
		Set<String> permanent_scopes = PERMANENT_GRANT.substitute(msg);
		Set<String> transient_scopes = TRANSIENT_GRANT.substitute(msg);
		Set<String> missing_scopes = MISSING.substitute(msg);
		
		if (transient_scopes == null) {
			transient_scopes = new HashSet<String>();
			
			transient_scopes.addAll(missing_scopes);
		}
		
		if (permanent_scopes != null) {
			transient_scopes.removeAll(permanent_scopes);
		}
		
		msg.put("scopes.allowed.transient", transient_scopes);
	}

	if (!result) {
		/* retrieve decoded permanent and transient grants */
		Set<String> permanent_scopes = PERMANENT_GRANT.substitute(msg);
		Set<String> transient_scopes = TRANSIENT_GRANT.substitute(msg);

		/* check if they are present */
		result = (permanent_scopes != null) && (transient_scopes != null);

		if (result) {
			/* if present, retrieve missing and ignored scopes */
			Set<String> ignored_scopes = DISCARDED.substitute(msg);
			Set<String> missing_scopes = MISSING.substitute(msg);

			missing_scopes.removeAll(permanent_scopes);
			missing_scopes.removeAll(transient_scopes);

			if (ignored_scopes != null) {
				missing_scopes.removeAll(ignored_scopes);
			}

			/* if empty, we can skip consent form */
			result = missing_scopes.isEmpty();
		}
	}

	return result;
}

package icdc.oauth

import java.util.Map.Entry

import javax.ws.rs.core.MultivaluedMap
import javax.ws.rs.core.UriBuilder

import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.dwe.http.HTTPPlugin
import com.vordel.el.Selector
import com.vordel.mime.HeaderSet

import groovy.transform.Field

@Field private static final Selector<String> OAUTH_REQUEST = SelectorResource.fromExpression("oauth.redirect.request", String.class);
@Field private static final Selector<String> LOGIN_URI = SelectorResource.fromExpression("oauth.consent.uri", String.class);
@Field private static final Selector<String> ID_TOKEN_HINT = SelectorResource.fromExpression("oauth.redirect.scopes", String.class);

@Field private static final Selector<MultivaluedMap> EXTRA_PARAMETERS = SelectorResource.fromExpression("oauth.request.extras.form", MultivaluedMap.class);

boolean invoke(Message msg) {
	MultivaluedMap<?,?> extras = EXTRA_PARAMETERS.substitute(msg)
	String id_token_hint = ID_TOKEN_HINT.substitute(msg);
	String signed = OAUTH_REQUEST.substitute(msg);
	String target = LOGIN_URI.substitute(msg);
	boolean result = false;
	
	if ((signed != null) && (target != null)) {
		HeaderSet headers = new HeaderSet();
		UriBuilder builder = UriBuilder.fromUri(new URI(target)).queryParam("request", signed);
		
		if (id_token_hint != null) {
			builder.queryParam("id_token_hint", id_token_hint);
		}
		
		if (extras != null) {
			for(Entry<?, List<?>> entry : extras.entrySet()) {
				String key = String.valueOf(entry.getKey());
				
				for(Object value : entry.getValue()) {
					if (value != null) {
						builder.queryParam(key, String.valueOf(value));
					}
				}
			}
		}
		
		/* set the new location header */
		headers.setHeader("Location", builder.build().toString());

		/* remove body and set redirect header */
		msg.remove(MessageProperties.CONTENT_BODY);
		msg.put(MessageProperties.HTTP_HEADERS, headers);

		/* set the 302 response */
		msg.put(MessageProperties.HTTP_RSP_STATUS, 302);
		msg.put(MessageProperties.HTTP_RSP_INFO, HTTPPlugin.getResponseText(302));
		
		result = true;
	}

	return result;
}

package icdc.oauth

import com.fasterxml.jackson.databind.node.ObjectNode
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.ScriptHelper
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody

import groovy.transform.Field

@Field private static final Selector<ObjectNode> OAUTH_PARAMETERS = SelectorResource.fromExpression("oauth.request.parsed.json", ObjectNode.class);

boolean invoke(Message msg) {
	/* retrieve mapped parameters and id_token_hint */
	ObjectNode request = OAUTH_PARAMETERS.substitute(msg).deepCopy();

	/* remove id_token_hint from parameters */
	request.remove("id_token_hint");

	/* set serialized request as JSON Body */
	msg.put(MessageProperties.CONTENT_BODY, ScriptHelper.toJSONBody(request));

	return true;
}

package icdc.oauth

import java.security.MessageDigest
import java.security.PrivateKey
import java.security.cert.X509Certificate

import com.fasterxml.jackson.databind.node.ObjectNode
import com.nimbusds.jose.JWSHeader
import com.nimbusds.jose.JWSVerifier
import com.nimbusds.jose.crypto.factories.DefaultJWSVerifierFactory
import com.nimbusds.jose.proc.JWSVerifierFactory
import com.nimbusds.jose.util.Base64URL
import com.nimbusds.jwt.JWTClaimsSet
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.ScriptHelper
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody
import com.vordel.security.cert.PersonalInfo
import com.vordel.store.cert.CertStore
import com.vordel.trace.Trace

import groovy.transform.Field

/* CertStore x5t map, all certificates are accepted */
@Field private static final Map<String,PersonalInfo> X5T_MAP = buildX5Ts("SHA-1");
@Field private static final Map<String,PersonalInfo> X5T256_MAP = buildX5Ts("SHA-256");

@Field private static final JWSVerifierFactory VERIFIERS = new DefaultJWSVerifierFactory();

@Field private static final Selector<String> OAUTH_REQUEST = SelectorResource.fromExpression("oauth.request.parsed.mapped['request']", String.class);
@Field private static final Selector<String> SERVICE_URI = SelectorResource.fromExpression("oauth.request.uri", String.class);

boolean invoke(Message msg) {
	boolean result = false;

	try {
		/* parse incoming request */
		SignedJWT signed = SignedJWT.parse(OAUTH_REQUEST.substitute(msg));
		JWSHeader header = signed.getHeader();

		/* signer certificate from x5t claims */
		X509Certificate signer = getX5TSigner(header);

		if (signer != null) {
			JWSVerifier verifier = VERIFIERS.createJWSVerifier(header, signer.getPublicKey());

			if (signed.verify(verifier)) {
				JWTClaimsSet claims = signed.getJWTClaimsSet();

				if (validateServiceClaims(msg, signer, claims)) {
					JSONBody body = ScriptHelper.toJSONBody(signed.getPayload().toString());
					ObjectNode node = body.getJSON();

					node.remove("iss");
					node.remove("sub");
					node.remove("aud");
					node.remove("exp");
					node.remove("nbf");
					node.remove("iat");
					node.remove("jti");

					msg.put(MessageProperties.CONTENT_BODY, body);

					/* save the jti (will be used as nonce for private requests) */
					msg.put("openid.request.jti", claims.getJWTID());
					msg.put("openid.request.signer", signer);

					result = true;
				}
			}
		}
	} catch(Exception any) {
		Trace.error("got error verifying signature", any);
	}

	return result;
}

public static boolean validateServiceClaims(Message msg, X509Certificate signer, JWTClaimsSet claims) {
	String iss = SERVICE_URI.substitute(msg);
	boolean validated = false;

	/* check that we produced this object */
	if ((iss != null) && iss.equals(claims.getIssuer())) {
		/* and compare dates */
		Date nbf = claims.getNotBeforeTime();
		Date exp = claims.getExpirationTime();
		Date now = new Date();

		/* check dates */
		if ((nbf != null) && (exp != null) && nbf.before(now) && exp.after(now)) {
			String response_type = claims.getStringClaim("response_type");
			String client_id = claims.getStringClaim("client_id");
			List<String> aud = claims.getAudience();

			/* check that we have at least 'response_type'  and 'client_id' */
			validated = (response_type != null) && (client_id != null) && (aud != null) && (aud.contains(iss));
		}
	}

	return validated;
}

public static X509Certificate getX5TSigner(JWSHeader header) {
	Base64URL x5t = header.getX509CertSHA256Thumbprint();
	X509Certificate signer = null;

	if (x5t != null) {
		PersonalInfo entry = X5T256_MAP.get(x5t.toString());

		signer = entry == null ? null : entry.certificate;
	} else {
		x5t = header.getX509CertThumbprint();

		if (x5t != null) {
			PersonalInfo entry = X5T_MAP.get(x5t.toString());

			signer = entry == null ? null : entry.certificate;
		}
	}

	return signer;
}

public static Map<String,PersonalInfo> buildX5Ts(String algorithm) {
	Map<String,PersonalInfo> x5ts = new HashMap<String,PersonalInfo>();
	CertStore store = CertStore.getInstance();

	if (store != null) {
		Map<String, PersonalInfo> aliases = store.getAliasToInfo();
		MessageDigest hash = MessageDigest.getInstance(algorithm);

		for(PersonalInfo info : aliases.values()) {
			byte[] digest = hash.digest(info.certificate.getEncoded());
			String x5t = Base64URL.encode(digest).toString();
			PersonalInfo previous = x5ts.get(x5t);

			if ((previous == null) || ((previous.privateKey == null) && (info.privateKey != null)) ) {
				x5ts.put(x5t, info);
			}
		}
	}

	return Collections.unmodifiableMap(x5ts);
}

package icdc.oauth

import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ArrayNode
import com.fasterxml.jackson.databind.node.ObjectNode
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody

import groovy.transform.Field

@Field private static final Selector<String> REQUEST = SelectorResource.fromExpression("oauth.redirect.request", String.class);
@Field private static final Selector<String> ID_TOKEN_HINT = SelectorResource.fromExpression("oauth.id_token.private", String.class);
@Field private static final Selector<Set> REQUESTED = SelectorResource.fromExpression("oauth.scopes.requested", Set.class);
@Field private static final Selector<Set> MISSING = SelectorResource.fromExpression("oauth.scopes.missing", Set.class);

@Field private static final Selector<Set> PERMANENT_GRANT = SelectorResource.fromExpression("scopes.allowed.persistent", Set.class);
@Field private static final Selector<Set> TRANSIENT_GRANT = SelectorResource.fromExpression("scopes.allowed.transient", Set.class);

@Field private static final ObjectMapper MAPPER = new ObjectMapper();

boolean invoke(Message msg) {
	ObjectNode request = parseJWT(REQUEST.substitute(msg));
	ObjectNode claims = parseJWT(ID_TOKEN_HINT.substitute(msg));
	ObjectNode body = ((JSONBody) msg.get(MessageProperties.CONTENT_BODY)).getJSON();
	ObjectNode scopes = body.putObject("scopes");

	putScopeArray(MISSING.substitute(msg), scopes, "missing");
	putScopeArray(REQUESTED.substitute(msg), scopes, "requested");
	putScopeArray(PERMANENT_GRANT.substitute(msg), scopes, "permanent");
	putScopeArray(TRANSIENT_GRANT.substitute(msg), scopes, "transient");

	Integer nbf = request == null ? null : request.path("nbf").numberValue();
	Integer exp = request == null ? null : request.path("exp").numberValue();
	String txn = claims == null ? null : claims.path("txn").asText(null);
	String sub = claims == null ? null : claims.path("sub").asText(null);

	if (nbf != null) {
		body.put("nbf", nbf);
	}

	if (exp != null) {
		body.put("exp", exp);
	}

	if (sub != null) {
		body.put("sub", sub);
	}

	if (txn != null) {
		body.put("txn", txn);
	}

	return true;
}

public static void putScopeArray(Set<String> scopeSet, ObjectNode scopeClaim, String arrayName) {
	if (scopeSet != null) {
		ArrayNode array = scopeClaim.putArray(arrayName);

		for(String scope : scopeSet) {
			array.add(scope);
		}
	}
}

public static ObjectNode parseJWT(String id_token) {
	ObjectNode claims = null;

	if (id_token != null) {
		SignedJWT signed = SignedJWT.parse(id_token);

		claims = MAPPER.readTree(signed.getPayload().toString());
	}

	return claims;
}

package icdc.oauth

import com.nimbusds.jwt.JWTClaimsSet
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.circuit.script.context.resources.KPSResource
import com.vordel.circuit.script.context.resources.KPSStoreResource
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.store.cert.CertStore

import groovy.transform.Field

@Field private static final KPSResource ID_TOKENS = getKPSResourceByAlias("OAuthIDTokens_V1");
@Field private static final Selector<String> ID_TOKEN = SelectorResource.fromExpression("oauth.id_token.public", String.class);
@Field private static final Selector<String> TXN = SelectorResource.fromExpression("oauth.id_token.txn", String.class);

boolean invoke(Message msg) {
	String id_token = ID_TOKEN.substitute(msg);

	SignedJWT signed = SignedJWT.parse(id_token);
	JWTClaimsSet claims = signed.getJWTClaimsSet();


	String txn = TXN.substitute(msg);
	String jti = claims.getJWTID();

	Map<String, Object> persisted = new HashMap<String, Object>();
	Long ttl = claims.getExpirationTime().getTime() - System.currentTimeMillis();

	if ((ttl % 1000L) != 0) {
		/* round to next second if needed */
		ttl += 1000L;
	}

	if (ttl > 0L) {
		/* convert to seconds */
		ttl /= 1000L;

		persisted.put("jti", jti);
		persisted.put("txn", txn);
		persisted.put("id_token", id_token);

		ID_TOKENS.createEntry(persisted, ttl.intValue());
	}

	return true;
}

public static KPSResource getKPSResourceByAlias(String alias) {
	/* XXX fix policy studio compilation */
	return CertStore.getInstance() == null ? null : new KPSStoreResource(alias);
}

