package icdc

import com.vordel.circuit.Message
import com.vordel.trace.Trace

boolean invoke(Message msg) {
	String redirect_uri = msg.get("params.query.redirect_uri");

	if ((redirect_uri != null) && (!redirect_uri.isEmpty())) {
		Trace.debug(" redirect_uri : "+ redirect_uri );

		try {
			URI uri = new URI(redirect_uri).normalize();

			msg.put("redirect_uri_temp", uri.toString());
			uri = new URI(uri.getScheme(), uri.getAuthority(), uri.getPath(), null, null);
			msg.put("params.query.redirect_uri", uri.toString())

			return true;
		} catch(URISyntaxException e) {
			Trace.error(e);
		}
	}

	return false;
}

importPackage(Packages.java.security);
importPackage(Packages.com.vordel.trace);
importPackage(Packages.java.util);
importPackage(Packages.org.apache.commons.codec.binary);

function invoke(msg)         {            

	var hash, hashBytes, hashBytesLeftHalf, atHashICDC ;

if (msg.get("accesstoken") == null)
{return true;
}
else {
	var accessToken = new java.lang.String(msg.get("accesstoken"));

Trace.debug("Access Token : " + accessToken);	

	try {

		hash = MessageDigest.getInstance("SHA-256");
		hash.reset();
		hash.update(accessToken.getBytes());

		hashBytes = hash.digest();
	 	Trace.debug("HASH AT : " +  new java.lang.String(hashBytes));

		hashBytesLeftHalf = java.util.Arrays.copyOf(hashBytes, hashBytes.length / 2);
		Trace.debug("LEFT HALF HASH AT : " + new java.lang.String(hashBytesLeftHalf));

		atHashICDC = org.apache.commons.codec.binary.Base64.encodeBase64URLSafeString(hashBytesLeftHalf);
		Trace.debug("LEFT HALF HASH AT base64 : " + atHashICDC);  

		msg.put("at.hash.icdc.claim", atHashICDC);

		return true;

	}  catch (e) {
		Trace.error(">>>Exception : " +e);
		msg.put("at.hash.icdc.claim", "");
		return true;
	}
 }
}

