Trace.debug(" redirect_uri : "+ redirect_uri );      
redirect_uri_tokenized= redirect_uri.tokenize("?");
redirect_uri_toverify = redirect_uri_tokenized[0];
msg.put("redirect_uri_temp", redirect_uri);
msg.put("params.query.redirect_uri", redirect_uri_toverify);

    } catch(Exception e) {
        Trace.error(e)
        return false
    }
    
    return true
}

