package icdc

import java.nio.charset.Charset
import java.security.MessageDigest
import java.security.NoSuchAlgorithmException

import com.vordel.circuit.CircuitAbortException
import com.vordel.circuit.Message
import com.vordel.circuit.oauth.token.OAuth2AccessToken
import com.vordel.trace.Trace

import groovy.transform.Field

@Field private static final String HASH_ALGORITHM = "SHA-256";
@Field private static final Charset UTF8 = Charset.forName("UTF-8");
@Field private static final Base64.Encoder ENCODER = Base64.getUrlEncoder().withoutPadding();

boolean invoke(Message msg) {
	OAuth2AccessToken accessToken = msg.get("accesstoken");

	if (accessToken == null) {
		Trace.debug("No Access Token");
	} else {
		Trace.debug("Access Token : " + accessToken.getValue());

		msg.put("at.hash.icdc.claim", accessToken != null ? getOpenIDHalfHash(HASH_ALGORITHM, accessToken.getValue()) : "");
	}

	return true;
}

public static final String getOpenIDHalfHash(String sigAlg, String value) throws CircuitAbortException {
	return value == null ? null : getOpenIDHalfHash(sigAlg, value.getBytes(UTF8));
}

public static final String getOpenIDHalfHash(String hashAlg, byte[] bytes) throws CircuitAbortException {
	String result = null;

	if (hashAlg != null) {
		try {
			MessageDigest e = MessageDigest.getInstance(hashAlg);

			e.reset();
			e.update(bytes);

			byte[] hash = e.digest();
			byte[] left = Arrays.copyOf(hash, hash.length >> 1);

			result = ENCODER.encodeToString(left);

			if (Trace.isDebugEnabled()) {
				Trace.debug("HASH AT : " +  ENCODER.encodeToString(hash));
				Trace.debug("LEFT HALF HASH AT : " + ENCODER.encodeToString(left));
			}
		} catch (NoSuchAlgorithmException e) {
			throw new CircuitAbortException("can't generate OpenID Hash", e);
		}
	}

	return result;
}

package icdc.oauth

 

import com.fasterxml.jackson.databind.JsonNode
import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ArrayNode
import com.fasterxml.jackson.databind.node.MissingNode
import com.fasterxml.jackson.databind.node.ObjectNode
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody
import com.fasterxml.jackson.databind.node.BooleanNode

 

import groovy.transform.Field

 

@Field private static final Selector<String> TYPE_ENVIRONNEMENT = SelectorResource.fromExpression("env.TYPE.ENVIRONNEMENT", String.class);
@Field private static final Selector<String> TXN = SelectorResource.fromExpression("openid.request.jti", String.class);
@Field private static final ObjectMapper MAPPER = new ObjectMapper();
@Field private static final Selector<String> NONCE = SelectorResource.fromExpression("oauth.request.nonce", String.class);


boolean invoke(Message msg) {
    ObjectNode generated = ((JSONBody) msg.get(MessageProperties.CONTENT_BODY)).getJSON();

 

    /* Set iat as long otherwise, groovy set the expression to return BigDecimal */    
    long iat = System.currentTimeMillis() / 1000L;
    
    ObjectNode root = MAPPER.createObjectNode();
    ObjectNode icdc = root.putObject("icdc");

 

    setIDTokenString(root, "txn", TXN.substitute(msg));
    setIDTokenClaim(root, "family_name", generated.path("nom"));
    setIDTokenClaim(root, "given_name", generated.path("prenom"));
    setIDTokenClaim(root, "email", generated.path("email"));
    setIDTokenClaim(root, "email_verified", BooleanNode.TRUE
);
    setIDTokenString(root, "nonce", NONCE.substitute(msg));

	JsonNode structures = generated.path("structures");
	
    root.put("auth_time", iat);
    root.putArray("amr").add("pwd");

	setIDTokenString(icdc, "urn:cdc:icdc:claims:applicationEnv", TYPE_ENVIRONNEMENT.substitute(msg));
	
    if (structures instanceof ArrayNode) {
		setIDTokenClaim(icdc, "urn:cdc:icdc.claims:structures", structures);
    }

    /* finally, update the message body */
    generated.removeAll();
    generated.setAll(root);
    
    return true;
}


private static void setIDTokenClaim(ObjectNode out, String name, JsonNode value) {
    if ((name != null) && (value != null) && (!(value instanceof MissingNode))) {
        out.set(name, value);
    }
}


private static void setIDTokenString(ObjectNode out, String name, String value) {
    if ((name != null) && (value != null)) {
        out.put(name, value);
    }
}

package icdc.oauth

 

import com.fasterxml.jackson.databind.JsonNode
import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ArrayNode
import com.fasterxml.jackson.databind.node.MissingNode
import com.fasterxml.jackson.databind.node.ObjectNode
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody

 

import groovy.transform.Field

 

@Field private static final Selector<String> TYPE_ENVIRONNEMENT = SelectorResource.fromExpression("env.TYPE.ENVIRONNEMENT", String.class);
@Field private static final Selector<String> TXN = SelectorResource.fromExpression("openid.request.jti", String.class);
@Field private static final ObjectMapper MAPPER = new ObjectMapper();

 

boolean invoke(Message msg) {
    ObjectNode generated = ((JSONBody) msg.get(MessageProperties.CONTENT_BODY)).getJSON();

 

    /* Set iat as long otherwise, groovy set the expression to return BigDecimal */    
    long iat = System.currentTimeMillis() / 1000L;
    
    ObjectNode root = MAPPER.createObjectNode();
    ObjectNode icdc = root.putObject("icdc");

 

    setIDTokenString(root, "txn", TXN.substitute(msg));
    setIDTokenClaim(root, "family_name", generated.path("nom"));
    setIDTokenClaim(root, "given_name", generated.path("prenom"));
    setIDTokenClaim(root, "email", generated.path("email"));

 

    JsonNode roles = generated.path("roles");
    JsonNode permissions = generated.path("permissions");
    
    if (roles instanceof ArrayNode) {
        roles = rolesAsStringArray(roles);
    }
    
    if (permissions instanceof ArrayNode) {
        permissions = permissionsAsStringArray(permissions);
    }
    
    root.put("auth_time", iat);
    root.putArray("amr").add("pwd");

 

    setIDTokenString(icdc, "urn:cdc:icdcclaims:applicationEnv", TYPE_ENVIRONNEMENT.substitute(msg));
    setIDTokenClaim(icdc, "urn:cdc::icdc.claims:roles", roles);
    setIDTokenClaim(icdc, "urn:cdc:icdc.claims:habilitations", permissions);
    

 

    /* finally, update the message body */
    generated.removeAll();
    generated.setAll(root);
    
    return true;
}

 

private static void setIDTokenClaim(ObjectNode out, String name, JsonNode value) {
    if ((name != null) && (value != null) && (!(value instanceof MissingNode))) {
        out.set(name, value);
    }
}

 

private static void setIDTokenString(ObjectNode out, String name, String value) {
    if ((name != null) && (value != null)) {
        out.put(name, value);
    }
}

 

private static ArrayNode rolesAsStringArray(ArrayNode objects) {
    ArrayNode out = MAPPER.createArrayNode();
    
    for(ObjectNode role : objects) {
        String code_role = role.path("code_role").asText(null);
        
        if (code_role != null) {
            out.add(code_role);
        }
    }
    
    return out;
}

 

private static ArrayNode permissionsAsStringArray(ArrayNode objects) {
    ArrayNode out = MAPPER.createArrayNode();
    
    for(ObjectNode permission : objects) {
        String code_permission = permission.path("code_permission").asText(null);
        
        if (code_permission != null) {
            out.add(code_permission);
        }
    }
    
    return out;
}

package icdc.oauth

 

import com.fasterxml.jackson.databind.JsonNode
import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.databind.node.ArrayNode
import com.fasterxml.jackson.databind.node.MissingNode
import com.fasterxml.jackson.databind.node.ObjectNode
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.mime.JSONBody

 

import groovy.transform.Field

 

@Field private static final Selector<String> TYPE_ENVIRONNEMENT = SelectorResource.fromExpression("env.TYPE.ENVIRONNEMENT", String.class);
@Field private static final Selector<String> TXN = SelectorResource.fromExpression("openid.request.jti", String.class);
@Field private static final ObjectMapper MAPPER = new ObjectMapper();

 

boolean invoke(Message msg) {
    ObjectNode generated = ((JSONBody) msg.get(MessageProperties.CONTENT_BODY)).getJSON();

 

    /* Set iat as long otherwise, groovy set the expression to return BigDecimal */    
    long iat = System.currentTimeMillis() / 1000L;
    
    ObjectNode root = MAPPER.createObjectNode();
    ObjectNode icdc = root.putObject("icdc");

 

    setIDTokenString(root, "txn", TXN.substitute(msg));
    setIDTokenClaim(root, "family_name", generated.path("nom"));
    setIDTokenClaim(root, "given_name", generated.path("prenom"));
    setIDTokenClaim(root, "email", generated.path("email"));


    JsonNode roles = generated.path("roles");
    JsonNode permissions = generated.path("permissions");
	JsonNode structures = generated.path("structures");
    
    if (roles instanceof ArrayNode) {
		setIDTokenClaim(icdc, "urn:cdc:icdc:claims:roles", roles);
    }
    
    if (permissions instanceof ArrayNode) {
		setIDTokenClaim(icdc, "urn:cdc:icdc:claims:habilitations", permissions);
    }


	if (structures instanceof ArrayNode) {
        structures = structuresAsStringArray(structures);
    }
	
    root.put("auth_time", iat);
    root.putArray("amr").add("pwd");

 

	setIDTokenString(icdc, "urn:cdc:icdc:claims:applicationEnv", TYPE_ENVIRONNEMENT.substitute(msg));
	setIDTokenClaim(icdc, "urn:cdc:icdc.claims:structures", structures);

    /* finally, update the message body */
    generated.removeAll();
    generated.setAll(root);
    
    return true;
}

 

private static void setIDTokenClaim(ObjectNode out, String name, JsonNode value) {
    if ((name != null) && (value != null) && (!(value instanceof MissingNode))) {
        out.set(name, value);
    }
}


private static void setIDTokenString(ObjectNode out, String name, String value) {
    if ((name != null) && (value != null)) {
        out.put(name, value);
    }
}



private static ArrayNode structuresAsStringArray(ArrayNode objects) {
    ArrayNode out = MAPPER.createArrayNode();
    
    for(ObjectNode structure : objects) {
        String codeRattachement = structure.path("codeRattachement").asText(null);
		String type = structure.path("type").asText(null);
		
        if (codeRattachement != null) {
            out.add(codeRattachement);
        }
		
        if (type != null) {
            out.add(type);
        }
    }
	
    
    return out;
}

package icdc

import com.vordel.circuit.Message
import com.vordel.el.Selector
import com.vordel.trace.Trace

import groovy.transform.Field

@Field private static final Selector<Long> EXPIRATION_TIME = new Selector<Long>(String.format("\$%s", "{expirationTime}"), Long.class);

boolean invoke(Message msg) {
	Long expirationDelay = EXPIRATION_TIME.substitute(msg);
	
	if (expirationDelay != null) {
		long now = System.currentTimeMillis();
		long exp = now + (expirationDelay.longValue() * 1000L);
	
		Trace.debug("expirationTime  : " + expirationDelay);
		Trace.debug("now  : " + now);
		Trace.debug("exp1  : " + exp);
		
		exp /= 1000L;
		
		Trace.debug("exp2  : " + exp);
		
		msg.put("now", Long.valueOf(now).toString());
		msg.put("expiration.time", Long.valueOf(exp).toString());
	
		return true;
	} else {
		return false;
	}
}

importPackage(Packages.com.vordel.trace);
importPackage(Packages.com.vordel.common.base64);


function invoke(msg)         {           
	try{
	var idToken = String(msg.get("params.query.id_token_hint"));
	Trace.debug(">>> idToken : " + idToken);

	var tokenParts = idToken.split(".");
	Trace.debug(">>> tokenParts : " + tokenParts[0]);
	Trace.debug(">>> tokenParts : " + tokenParts[1]);

	//PAD Base64
	var base64data = tokenParts[1];

    	while (base64data.length % 4 !== 0) {
      		base64data+= "=";
	}

	Trace.debug(">>> base64data : " + base64data);

	var claimsJson = Decoder.decodeToString(base64data);
	Trace.debug(">>> claimsJson : " + claimsJson);

	var claims = JSON.parse(claimsJson);

	var aud = claims.aud;
       var sub=  claims.sub;

	Trace.debug(">>> aud : " + aud);
	Trace.debug(">>> sub : " + sub);

	msg.put("id.token.claim.aud",  aud);
       msg.put("id.token.claim.sub",  sub );

	return true;   
	}  catch (e) {
	Trace.error(">>>Exception : " +e);
	 return false;
	}

}

package icdc

import com.nimbusds.jose.util.Base64URL
import com.nimbusds.jwt.JWTClaimsSet
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.trace.Trace

boolean invoke(Message msg) {
	String idToken = msg.get("params.query.id_token_hint")

	if (idToken == null) {
		Trace.debug("No ID Token");
	} else {
		Trace.debug(">>> ID Token : " + idToken);

		SignedJWT parsed = SignedJWT.parse(idToken);
		JWTClaimsSet claimsJson = parsed.getJWTClaimsSet();

		String sub = claimsJson.getSubject();
		String aud = null;

		List<String> audiences = claimsJson.getAudience();

		if ((audiences != null) && (!audiences.isEmpty())) {
			/* XXX limit to the first audience for now */
			aud = audiences.get(0);
		}

		if (Trace.isDebugEnabled())	{
			Base64URL tokenParts = parsed.getParsedParts();

			Trace.debug(">>> tokenParts : " + tokenParts[0].toString());
			Trace.debug(">>> tokenParts : " + tokenParts[1].toString());
			Trace.debug(">>> claimsJson : " + claimsJson.toString());
		}

		msg.put("id.token.claim.aud", aud);
		msg.put("id.token.claim.sub", sub);
	}

	return true;
}

