	try         { 
	def username = msg.get("username");
	def password= msg.get("password");           
	username = username.trim();
	password = password.trim();
	msg.put("username", username );
	msg.put("password= ", password );
	return true;
	} catch(Exception e) {
        Trace.error(e)
        return false
    }
    
    return true
}

