package icdc.oauth

import com.vordel.circuit.Message
import com.vordel.circuit.oauth.token.AuthorizationCode
import com.vordel.circuit.oauth.token.OAuth2RefreshToken
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector

import groovy.transform.Field

@Field private static final Selector<String> GRANT_TYPE = SelectorResource.fromExpression("oauth.request.parsed.mapped['grant_type']", String.class);

boolean invoke(Message msg) {
	Map<String, String> info = null;
	String id_token = null;

	/* ensure all token metadata remains private by providing an empty public set */
	msg.put("oauth.response.claims.public", new HashSet<String>());

	if ("authorization_code".equals(GRANT_TYPE.substitute(msg))) {
		AuthorizationCode code = msg.get("authzcode");

		if ((code != null) && (code.getScopes().contains("openid"))) {
			info = code.getAdditionalInformation();
		}
	} else if ("refresh_token".equals(GRANT_TYPE.substitute(msg))) {
		OAuth2RefreshToken refresh_token = msg.get("oauth.request.refresh_token");

		if (refresh_token != null) {
			info = refresh_token.getAdditionalInformation();
		}
	}

	if (info != null) {
		/*
		 * Retrieve private ID Token
		 */
		id_token = info.get("oauth.id_token.private");

		/* retrieve authorize service uri for signer alternate audience */
		msg.put("oauth.authorize.uri", info.get("oauth.authorize.uri"));

		/*
		 * also, try to retrieve claims object and nonce
		 */
		msg.put("oauth.id_token.nonce", info.get("internalstorage.openid.nonce"));
		msg.put("oauth.id_token.claims", info.get("oauth.id_token.claims"));
		msg.put("oauth.id_token.private", id_token);
	}

	return id_token != null;
}

package vordel

import com.vordel.circuit.Message
import com.vordel.circuit.oauth.token.OAuth2AccessToken
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector

import groovy.transform.Field

@Field private static final Selector<String> GRANT_TYPE = SelectorResource.fromExpression("oauth.request.parsed.mapped['grant_type']", String.class);

boolean invoke(Message msg) {
	String id_token = msg.get("oauth.id_token.public");
	OAuth2AccessToken token = msg.get("accesstoken");

	if ((id_token != null) && (token != null)) {
		token.setAdditionalInformation("id_token", id_token);
		token.setIdToken(id_token);
	}

	return true;
}

package icdc.refactored

import com.vordel.circuit.Message
import com.vordel.circuit.oauth.token.OAuth2AccessToken
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector

import groovy.transform.Field

@Field private static final Selector<OAuth2AccessToken> OAUTH_AT = SelectorResource.fromExpression("accesstoken", OAuth2AccessToken.class);

boolean invoke(Message msg) {
	OAuth2AccessToken accesstoken = OAUTH_AT.substitute(msg);

	if ((accesstoken != null) && accesstoken.hasRefresh()) {
		/* remove existing refresh token */
		accesstoken.setOAuth2RefreshToken(null);
	}

	return true;
}

package icdc

import java.nio.charset.Charset
import java.security.MessageDigest
import java.security.NoSuchAlgorithmException

import com.vordel.circuit.CircuitAbortException
import com.vordel.circuit.Message
import com.vordel.circuit.oauth.token.OAuth2AccessToken
import com.vordel.trace.Trace

import groovy.transform.Field

@Field private static final String HASH_ALGORITHM = "SHA-256";
@Field private static final Charset UTF8 = Charset.forName("UTF-8");
@Field private static final Base64.Encoder ENCODER = Base64.getUrlEncoder().withoutPadding();

boolean invoke(Message msg) {
	OAuth2AccessToken accessToken = msg.get("accesstoken");

	if (accessToken == null) {
		Trace.debug("No Access Token");
	} else {
		Trace.debug("Access Token : " + accessToken.getValue());

		msg.put("at.hash.icdc.claim", accessToken != null ? getOpenIDHalfHash(HASH_ALGORITHM, accessToken.getValue()) : "");
	}

	return true;
}

public static final String getOpenIDHalfHash(String sigAlg, String value) throws CircuitAbortException {
	return value == null ? null : getOpenIDHalfHash(sigAlg, value.getBytes(UTF8));
}

public static final String getOpenIDHalfHash(String hashAlg, byte[] bytes) throws CircuitAbortException {
	String result = null;

	if (hashAlg != null) {
		try {
			MessageDigest e = MessageDigest.getInstance(hashAlg);

			e.reset();
			e.update(bytes);

			byte[] hash = e.digest();
			byte[] left = Arrays.copyOf(hash, hash.length >> 1);

			result = ENCODER.encodeToString(left);

			if (Trace.isDebugEnabled()) {
				Trace.debug("HASH AT : " +  ENCODER.encodeToString(hash));
				Trace.debug("LEFT HALF HASH AT : " + ENCODER.encodeToString(left));
			}
		} catch (NoSuchAlgorithmException e) {
			throw new CircuitAbortException("can't generate OpenID Hash", e);
		}
	}

	return result;
}

package icdc.refactored

import com.vordel.circuit.Message
import com.vordel.circuit.oauth.token.OAuth2AccessToken
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector

import groovy.transform.Field

@Field private static final Selector<String> GRANT_TYPE = SelectorResource.fromExpression("oauth.request.parsed.mapped['grant_type']", String.class);
@Field private static final Selector<OAuth2AccessToken> OAUTH_AT = SelectorResource.fromExpression("accesstoken", OAuth2AccessToken.class);

boolean invoke(Message msg) {
	String grant_type = GRANT_TYPE.substitute(msg);
	boolean result = false;
	
	if ("authorization_code".equals(grant_type) || "refresh_token".equals(grant_type)) {
		/* if we have an openid compatible flow, check token scopes */
		OAuth2AccessToken accesstoken = OAUTH_AT.substitute(msg);
		
		if ((accesstoken != null) && (accesstoken.getScope().contains("openid"))) {
			/* openid scope is set, create id token */
			result = true;
		}
	}

	return result;
}

import com.vordel.kps.impl.KPS
import com.vordel.kps.query.KeyQuery;
import com.vordel.kps.ObjectNotFound
import com.vordel.trace.Trace
import com.vordel.circuit.oauth.common.OAuth2Utils
import com.vordel.common.apiserver.discovery.model.OAuthAppScope
import java.util.ArrayList

def invoke(msg) {
    	
    try {
	def clientId = msg.get("params.query.client_id");
        
       def strScopes = msg.get("params.query.scope");
       ArrayList requestedScopes = null;
       if (strScopes != null){
           requestedScopes = strScopes .tokenize(" ");
           Trace.info(" requested scopes: "+ requestedScopes );
       }     
      def app = OAuth2Utils.getAppDetailsFromClientId(msg, clientId);
      def oauthScopes = app.getDefaultScopes();
      ArrayList defaultScopes = new ArrayList();
      oauthScopes.each { scope ->
          defaultScopes.add(scope.getScope());
      }
      defaultScopes.unique();
	Trace.info(" default scopes: "+ defaultScopes);      

	def kpsTable = KPS.getInstance().getModel().getAliases().get("ClientApplicationRegistry");

	def application= kpsTable.getCached(clientId);
      def applicationId = application.get("applicationId");
       Trace.info(" applicationId   "+ applicationId );  
       def query = new KeyQuery("applicationId", applicationId );

     def kpsTableRes = KPS.getInstance().getModel().getAliases().get("PortalOAuthResourcesStore");
	ArrayList scopes= new ArrayList();
	def tx = kpsTableRes .beginTransaction();
       
      def ressources = tx.query(query);
	ressources.each { item ->
            def scope =  item.get("uriprefix");
		scopes.add(scope);
      } 
      scopes = scopes.sort().intersect(defaultScopes.sort());
      Trace.info(" intercept1 scopes: "+ scopes);  
      if (requestedScopes  != null) {
          scopes = scopes.sort().intersect(requestedScopes.sort() );
	    Trace.info("  intercept2 scopes: "+ scopes);  
      }
	
	if( scopes.size() > 0 ) {
	   msg.put("scopes.for.token", new java.util.HashSet(scopes));
      }
	Trace.info(" scopes: "+ scopes);               
    } catch(Exception e) {
        Trace.error(e)
        return false
    }
    
    return true
}

package icdc

import com.vordel.circuit.Message
import com.vordel.circuit.oauth.common.OAuth2Utils
import com.vordel.circuit.oauth.common.OAuthScopeUtils
import com.vordel.circuit.oauth.kps.ApplicationDetails
import com.vordel.common.apiserver.discovery.model.OAuthAppScope
import com.vordel.kps.Store
import com.vordel.kps.Transaction
import com.vordel.kps.impl.KPS
import com.vordel.kps.query.KeyQuery
import com.vordel.trace.Trace

boolean invoke(Message msg) {
	try {
		String clientId = msg.get("params.query.client_id");

		String rawScopes = msg.get("params.query.scope");
		Set<String> scopesInMessage = null;

		if (rawScopes != null) {
			scopesInMessage = OAuthScopeUtils.parseParameterList(rawScopes);

			Trace.info(String.format("requested scopes: %s", scopesInMessage));
		}

		ApplicationDetails app = OAuth2Utils.getAppDetailsFromClientId(msg, clientId);
		List<OAuthAppScope> oauthScopes = app.getDefaultScopes();
		Set<String> defaultScopes = new TreeSet<String>();

		for(OAuthAppScope scope : oauthScopes) {
			defaultScopes.add(scope.getScope());
		}

		Trace.info(String.format("default scopes: %s", defaultScopes));

		Store kpsTable = KPS.getInstance().getModel().getAliases().get("ClientApplicationRegistry");

		Map<String, Object> application= kpsTable.getCached(clientId);
		String applicationId = application.get("applicationId");

		Trace.info(String.format("applicationId   ", applicationId));

		KeyQuery query = new KeyQuery("applicationId", applicationId );
		Store kpsTableRes = KPS.getInstance().getModel().getAliases().get("PortalOAuthResourcesStore");
		Transaction tx = kpsTableRes .beginTransaction();
		Set<String> scopes= new TreeSet<String>();

		try {
			Iterable<Map<String, Object>> ressources = tx.query(query);

			for(Map<String, Object> item : ressources) {
				String  scope =  item.get("uriprefix");

				scopes.add(scope);
			}
		} finally {
			tx.close();
		}

		scopes.retainAll(defaultScopes);

		Trace.info(String.format("intercept1 scopes: %s", scopes));

		if (scopesInMessage  != null) {
			scopes.retainAll(scopesInMessage);

			Trace.info(String.format("intercept2 scopes: %s", scopes));
		}

		if( scopes.size() > 0 ) {
			msg.put("scopes.for.token", new HashSet(scopes));
		}

		Trace.info(" scopes: "+ scopes);
	} catch(Exception e) {
		Trace.error(e)
		return false
	}

	return true
}

