import com.vordel.circuit.Message
import com.vordel.circuit.oauth.kps.ApplicationDetails
import com.vordel.common.apiserver.model.Application

boolean invoke(Message msg) {
	ApplicationDetails details = msg.get("oauth.client.details");
	String codeAramis = null;
	
	if (details != null) {
		Application application = details.getApplication();
		Map<String, String> tags = application.getTags();
		
		msg.put("apimgmt.application", application);
		msg.put("idApp", toLowerCase(tags.get("idApplication")));
		msg.put("codeApp", codeAramis = toLowerCase(tags.get("codeAramis")));
	}
	
	return codeAramis != null;
}

public static String toLowerCase(String value) {
	return value == null ? null : value.toLowerCase();
}

var CLIENT_ID_SELECTOR = com.vordel.circuit.script.context.resources.SelectorResource.fromExpression("oauth.request.client_id", java.lang.String.class);

function invoke(msg) {
	/* retrieve client_id from message*/
	var client_id = CLIENT_ID_SELECTOR.substitute(msg);
	
	/* retrieve application details (wille set 'oauth.client.details' attribute) */
	com.vordel.sdk.plugins.oauth2.OAuthGuavaCache.getAppDetailsFromClientId(msg, client_id);
	
	return true;
}

importPackage(Packages.java.security);
importPackage(Packages.com.vordel.trace);
importPackage(Packages.java.util);
importPackage(org.apache.commons.codec.binary.*);
importPackage (java.security.*);

function invoke(msg)         
{       
	//On récupère le code_challenge et code_challenge_method dans le cache temporaire de la request authorize
	var code_challenge = msg.get("code_challenge");
	var code_challenge_method = msg.get("code_challenge_method");
       var codeVerifier = msg.get("params.query.code_verifier");
 	Trace.debug("code_challenge:"+code_challenge);
	Trace.debug("code_challenge_method:"+code_challenge_method );
	Trace.debug("codeVerifier :"+codeVerifier  );

	if (code_challenge != null && code_challenge !='') {
		 if ( code_challenge_method != null && code_challenge_method.equals("plain")  ) {
			  if(  codeVerifier != null && codeVerifier.equals(code_challenge) ) { 
				return true ;
	   		  } else {
				return false;
	   		  }	 
		} else {

		      var sha256Digester = java.security.MessageDigest.getInstance("SHA-256");
	             sha256Digester.update(codeVerifier.getBytes("ISO_8859_1"));
       	      var digestBytes = sha256Digester.digest();
                    var controlcode_challenge = org.apache.commons.codec.binary.Base64.encodeBase64URLSafeString(digestBytes);

  		     Trace.debug("controlcode_challenge:"+controlcode_challenge );

			if( controlcode_challenge.equals(code_challenge) ) { 
				return true ;
	   		  } else {
				return false;
	   		  }	 
		}
	}
	else{
		// Pas de code_challenge dans la requete authorize d'origine
		return true
	}        
}

package icdc

import java.nio.charset.Charset
import java.security.MessageDigest
import java.security.NoSuchAlgorithmException

import com.vordel.circuit.Message
import com.vordel.trace.Trace

import groovy.transform.Field

@Field public static final Charset ISO8859 = Charset.forName("ISO_8859_1");
@Field private static final Base64.Decoder DECODER = Base64.getUrlDecoder();

boolean invoke(Message msg) {
	String code_challenge = nonEmpty(msg.get("code_challenge"));
	String code_challenge_method = nonEmpty(msg.get("code_challenge_method"));
	String codeVerifier = nonEmpty(msg.get("params.query.code_verifier"));
	
	return checkCodeVerifier(code_challenge, code_challenge_method, codeVerifier);
}

protected static String nonEmpty(String value) {
	return (value == null) || value.isEmpty() ? null : value;
}

protected static boolean checkCodeVerifier(String code_challenge, String code_challenge_method, String code_verifier) {
	boolean valid = true;
	
	if ((code_challenge != null) && (code_challenge_method != null)) {
		/* code challenge is here... check it */
		valid &= code_verifier != null;
		
		if (valid) {
			/* check the code verifier */
			if ("plain".equals(code_challenge_method)) {
				valid = code_verifier.equals(code_challenge);
			} else if ("S256".equals(code_challenge_method)) {
				try {
					MessageDigest digester = MessageDigest.getInstance("SHA-256");

					digester.update(code_verifier.getBytes(ISO8859));

					byte[] challenge = DECODER.decode(code_challenge);
					byte[] digest = digester.digest();

					valid = (challenge != null) && (digest != null) && Arrays.equals(challenge, digest);
				} catch (NoSuchAlgorithmException e) {
					Trace.error("Unexpected Exception", e);
				}
			} else {
				valid = false;
			}
		}
	}
	
	return valid;
}

package icdc

import java.nio.charset.Charset
import java.security.MessageDigest
import java.security.NoSuchAlgorithmException

import com.vordel.circuit.CircuitAbortException
import com.vordel.circuit.Message
import com.vordel.circuit.oauth.token.OAuth2AccessToken
