import groovy.transform.Field

@Field private static final String HASH_ALGORITHM = "SHA-256";
@Field private static final Charset UTF8 = Charset.forName("UTF-8");
@Field private static final Base64.Encoder ENCODER = Base64.getUrlEncoder().withoutPadding();

boolean invoke(Message msg) {
	OAuth2AccessToken accessToken = msg.get("accesstoken");

	if (accessToken == null) {
		Trace.debug("No Access Token");
	} else {
		Trace.debug("Access Token : " + accessToken.getValue());

		msg.put("at.hash.icdc.claim", accessToken != null ? getOpenIDHalfHash(HASH_ALGORITHM, accessToken.getValue()) : "");
	}

	return true;
}

public static final String getOpenIDHalfHash(String sigAlg, String value) throws CircuitAbortException {
	return value == null ? null : getOpenIDHalfHash(sigAlg, value.getBytes(UTF8));
}

public static final String getOpenIDHalfHash(String hashAlg, byte[] bytes) throws CircuitAbortException {
	String result = null;

	if (hashAlg != null) {
		try {
			MessageDigest e = MessageDigest.getInstance(hashAlg);

			e.reset();
			e.update(bytes);

			byte[] hash = e.digest();
			byte[] left = Arrays.copyOf(hash, hash.length >> 1);

			result = ENCODER.encodeToString(left);

			if (Trace.isDebugEnabled()) {
				Trace.debug("HASH AT : " +  ENCODER.encodeToString(hash));
				Trace.debug("LEFT HALF HASH AT : " + ENCODER.encodeToString(left));
			}
		} catch (NoSuchAlgorithmException e) {
			throw new CircuitAbortException("can't generate OpenID Hash", e);
		}
	}

