package vordel

import com.nimbusds.jose.JOSEException
import com.nimbusds.jose.JWSAlgorithm
import com.nimbusds.jose.JWSHeader
import com.nimbusds.jose.crypto.MACSigner
import com.nimbusds.jose.crypto.MACVerifier
import com.nimbusds.jwt.JWTClaimsSet
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.trace.Trace

import groovy.transform.Field

/* selectors for group key and incoming request object */
@Field private static final Selector<String> OAUTH_REQUEST = SelectorResource.fromExpression("oauth.form.request", String.class);
@Field private static final Selector<String> JWS_KEY = SelectorResource.fromExpression("oauth.jws.key", String.class);

boolean invoke(Message msg) {
	JWTClaimsSet claims = null;
	String client_id = null;
	URI oauth_uri = null;

	try {
		/* parse incoming request */
		SignedJWT signed = SignedJWT.parse(OAUTH_REQUEST.substitute(msg));
		JWSHeader header = signed.getHeader();

		/* retrieve, trim and pad secret key */
		byte[] secretKey = padKey(header.getAlgorithm(), JWS_KEY.substitute(msg).getBytes());
		MACVerifier verifier = new MACVerifier(secretKey);

		if (signed.verify(verifier)) {
			/* if signature is valid, retrieve claims */
			claims = signed.getJWTClaimsSet();

			/* and compare dates */
			Date nbf = claims.getNotBeforeTime();
			Date exp = claims.getExpirationTime();
			Date now = new Date();

			if ((nbf != null) && (exp != null) && nbf.before(now) && exp.after(now)) {
				client_id = claims.getStringClaim("client_id");
				oauth_uri = URI.create(claims.getIssuer());
			}
		}
	} catch(Exception any) {
		Trace.error("got error verifying signature", any);
	}

	if (claims != null) {
		msg.put("oauth.request.claims", claims);

		if (oauth_uri != null) {
			msg.put("oauth.request.uri", oauth_uri);
		}
		
		if (client_id != null) {
			msg.put("oauth.request.client_id", client_id);
		}
	}

	return oauth_uri != null;
}

public static final byte[] padKey(JWSAlgorithm algorithm, byte[] key) throws JOSEException {
	int requested = MACSigner.getMinRequiredSecretLength(algorithm);
	int available = key.length * 8;

	if (requested > available) {
		/* pad the key with '0' or trim it if needed */
		ByteArrayOutputStream out = new ByteArrayOutputStream();
		ByteArrayInputStream inp = new ByteArrayInputStream(key);

		for (int read = -1; (requested > 0) && (read = inp.read()) != -1; requested -= 8) {
			out.write(read);
		}

		for (; requested > 0; requested -= 8) {
			out.write(0);
		}

		key = out.toByteArray();
	}

	return key;
}

package vordel

import java.nio.charset.Charset

import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.dwe.ByteArrayContentSource
import com.vordel.el.Selector
import com.vordel.mime.Body

import groovy.transform.Field

@Field private static final Selector<String> OAUTH_TEMPLATE = SelectorResource.fromExpression(MessageProperties.CONTENT_BODY, String.class);
@Field private static final Charset UTF8 = Charset.forName("UTF-8");

boolean invoke(Message msg) {
	String template = OAUTH_TEMPLATE.substitute(msg);
	Body body = msg.get(MessageProperties.CONTENT_BODY);

	template = SelectorResource.fromLiteral(template, String.class, false).substitute(msg);
	body = Body.create(body.getHeaders(), body.getContentType(), new ByteArrayContentSource(template.getBytes(UTF8)));

	msg.put(MessageProperties.CONTENT_BODY, body);

	return true;
}

