function invoke(msg) {
	/* retrieve client_id from message*/
	var client_id = CLIENT_ID_SELECTOR.substitute(msg);
	
	/* retrieve application details (wille set 'oauth.client.details' attribute) */
	com.vordel.sdk.plugins.oauth2.OAuthGuavaCache.getAppDetailsFromClientId(msg, client_id);
	
	return true;
}

package vordel

import com.fasterxml.jackson.databind.node.ObjectNode
import com.nimbusds.jose.JOSEException
import com.nimbusds.jose.JWSAlgorithm
import com.nimbusds.jose.JWSHeader
import com.nimbusds.jose.crypto.MACSigner
import com.nimbusds.jose.crypto.MACVerifier
