package vordel

import com.nimbusds.jose.JOSEException
import com.nimbusds.jose.JWSAlgorithm
import com.nimbusds.jose.JWSHeader
import com.nimbusds.jose.crypto.MACSigner
import com.nimbusds.jose.crypto.MACVerifier
import com.nimbusds.jwt.SignedJWT
import com.vordel.circuit.Message
import com.vordel.circuit.MessageProperties
import com.vordel.circuit.script.ScriptHelper
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector
import com.vordel.trace.Trace

import groovy.transform.Field

/* selectors for group key and incoming request object */
@Field private static final Selector<String> OAUTH_REQUEST = SelectorResource.fromExpression("oauth.request.parsed.mapped['request']", String.class);
@Field private static final Selector<String> JWS_KEY = SelectorResource.fromExpression("oauth.jws.key", String.class);

boolean invoke(Message msg) {
	boolean result = false;

	try {
		/* parse incoming request */
		SignedJWT signed = SignedJWT.parse(OAUTH_REQUEST.substitute(msg));
		JWSHeader header = signed.getHeader();

		/* retrieve, trim and pad secret key */
		byte[] secretKey = padKey(header.getAlgorithm(), JWS_KEY.substitute(msg).getBytes());
		MACVerifier verifier = new MACVerifier(secretKey);

		result = signed.verify(verifier);

		if (result) {
			msg.put(MessageProperties.CONTENT_BODY, ScriptHelper.toJSONBody(signed.getPayload().toString()));
		}
	} catch(Exception any) {
		Trace.error("got error verifying signature", any);
	}

	return result;
}

