		/* pad the key with '0' or trim it if needed */
		ByteArrayOutputStream out = new ByteArrayOutputStream();
		ByteArrayInputStream inp = new ByteArrayInputStream(key);

		for (int read = -1; (requested > 0) && (read = inp.read()) != -1; requested -= 8) {
			out.write(read);
		}

		for (; requested > 0; requested -= 8) {
			out.write(0);
		}

		key = out.toByteArray();
	}

	return key;
}

package vordel

import com.vordel.circuit.Message
import com.vordel.circuit.oauth.token.AuthorizationCode
import com.vordel.circuit.script.context.resources.SelectorResource
import com.vordel.el.Selector

import groovy.transform.Field

@Field private static final Selector<String> GRANT_TYPE = SelectorResource.fromExpression("oauth.request.parsed.mapped['grant_type']", String.class);

boolean invoke(Message msg) {
	boolean generate = false;

	if ("authorization_code".equals(GRANT_TYPE.substitute(msg))) {
		AuthorizationCode code = msg.get("authzcode");

		if (code != null) {
			Map<String, String> claims = code.getAdditionalInformation();

			generate = (claims != null) && (!claims.containsKey("id_token"));
		}
	}

	return generate;
}

package vordel

import com.vordel.circuit.Message
