importPackage(Packages.org.w3c.dom);
importPackage(Packages.com.vordel.mime);

// This function moves the <enc:ReferenceList> to be before the <dsig:Signature> node.
// This script is useful for WSE 3.0 (.NET) integration when a message that it signed, then
// encrypted is required. When you run the signature generation filter, then the encryption
// filter, the <enc:ReferenceList> will be placed after the <dsig:Signature>. WSE 3.0 requires
// it to be placed before the <dsig:Signature>.
// This function uses the DOM API to move the nodes in the message as required.

function invoke(msg)        
{     
    var doc = XMLBody.getDocument(msg);

    // Get the <soap:Header> node
    
    var nodes = doc.getDocumentElement().getElementsByTagNameNS(
                                   "http://schemas.xmlsoap.org/soap/envelope/", "Header");
    if (nodes .getLength() == 0)
        return false;
    var headEle = nodes.item(0);

    // Get the <wsse:Security> node
    nodes = headEle.getElementsByTagNameNS("http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-wssecurity-secext-1.0.xsd", "Security");
    if (nodes .getLength() == 0)
        return false;
    var wsseEle = nodes.item(0);
   
    // Get the <dsig:Signature> node
    nodes = wsseEle.getElementsByTagNameNS("http://www.w3.org/2000/09/xmldsig#", "Signature");   
    if (nodes .getLength() == 0)
        return false;
    var signatureEle = nodes.item(0);
 
    // Get the <enc:ReferenceList>  node
    nodes = wsseEle.getElementsByTagNameNS("http://www.w3.org/2001/04/xmlenc#", "ReferenceList");  
    if (nodes .getLength() == 0)
        return false;
    var refListEle = nodes.item(0);

    // Remove the <enc:ReferenceList>  node from its current position
    var removed = doc.removeChild(refListEle);   
 
    // Place the <enc:ReferenceList>  node before the <dsig:Signature> node
    doc.insertBefore(removed, signatureEle );
    return true;      
}

importPackage(Packages.java.util);
importPackage(Packages.java.nio.charset);

function invoke(msg)
{
    var data = msg.get("data.base64");
    var base64DecodedData = Base64.getDecoder().decode(data);
    var dataString = new java.lang.String(base64DecodedData, StandardCharsets.UTF_8);
    msg.put("data.decoded", dataString);
    return true;
}

import com.vordel.trace.Trace;

def invoke(msg)        
{            
    Trace.error("This trace statement was generated in javascript!");
    Trace.debug("This trace statement was generated in javascript!");
    return true;         
}

var imp = new JavaImporter(com.vordel.mime);
with(imp) {
    function invoke(msg) {
        var doc = XMLBody.getDocument(msg);
        return true;
    }
};

def invoke(msg) 
{ 
    def userProperties = msg.get("attribute.lookup.list");

    if (userProperties == null) {
        userProperties = new java.util.HashMap();
        msg.put("attribute.lookup.list", userProperties);
    }
    userProperties.put("role", "Manager");
    return true;
}

var imp = new JavaImporter(com.vordel.trace);
with(imp) {
    function invoke(msg) {

        Trace.error("This trace statement was generated in javascript!");
        Trace.debug("This trace statement was generated in javascript!");
        return true;
    }
};

def invoke(msg):
    httpHeaders = msg.get("http.headers")
    action = httpHeaders.get("SOAPAction")
    if action != None:
        return action.equals("test")
    return False

import com.vordel.trace.Trace;
import java.util.Calendar;
import java.util.GregorianCalendar;

def invoke(msg)         {       
    def notBeforeMins = 9 * 60;   // 9am
    def notAfterMins = 17 * 60;   // 5pm
    def notBeforeDay = Calendar.MONDAY;   // 2
    def notAfterDay = Calendar.FRIDAY;  // 6
    def calNow = new GregorianCalendar();
    def hour = calNow.get(Calendar.HOUR_OF_DAY);
    def minute = calNow.get(Calendar.MINUTE);
    def minsNow = (hour * 60) + minute;
    def dayOfWeek = calNow.get(Calendar.DAY_OF_WEEK);

    if (dayOfWeek < notBeforeDay || dayOfWeek > notAfterDay) {
        Trace.debug("Tried to access the web service at the weekend");
        return false;

    }

    if  (minsNow < notBeforeMins) {
        Trace.debug("Tried to access the web service before 9am");
        return false;

    }

    if  (minsNow > notAfterMins) {
        Trace.debug("Tried to access the web service after 5pm");
        return false;
    }

    return true;         
}

var base64Import = new JavaImporter(java.util.Base64, java.nio.charset);

with(base64Import){
    function invoke(msg)
    {
        var data = msg.get("data.base64");
        var base64DecodedData = Base64.getDecoder().decode(data);
        var dataString = new java.lang.String(base64DecodedData, StandardCharsets.UTF_8);
        msg.put("data.decoded", dataString);
        return true;
    }
};

importPackage(Packages.java.util);
importPackage(Packages.java.nio.charset);

function invoke(msg)
{
    var data = msg.get("data");
    var base64EncodedData = Base64.getEncoder().encodeToString(data.getBytes(StandardCharsets.UTF_8));
    msg.put("data.base64", base64EncodedData);
    return true;
}

var base64Import = new JavaImporter(java.util.Base64, java.nio.charset);

with(base64Import){
    function invoke(msg)
    {
        var data = msg.get("data");
        var base64EncodedData = Base64.getEncoder().encodeToString(data.getBytes(StandardCharsets.UTF_8));
        msg.put("data.base64", base64EncodedData);
        return true;
    }
};

function invoke(msg) {
    var httpHeaders = msg.get("http.headers");
    var action = httpHeaders.get("SOAPAction");
    return action == "test";
}

function invoke(msg) {
    var userProperties = msg.get("attribute.lookup.list");

    if (userProperties == null) {
        userProperties = new java.util.HashMap();
        msg.put("attribute.lookup.list", userProperties);
    }
    var role = userProperties.get("role");
    return role == "Manager";
}

package vordel

import com.vordel.circuit.Message
import com.vordel.dwe.Service
import com.vordel.store.cert.CertStore

import groovy.transform.Field

@Field private static final String SHARED_KEY = getSharedKey();

boolean invoke(Message msg) {
	msg.put("oauth.jws.key", SHARED_KEY);

	return true;
}

public static String getSharedKey() {
	if (CertStore.getInstance() != null) {
		Service service = Service.getInstance();

		return String.format("%s_%s", service.getDomainID(), service.getGroupID());
	}

	return null;
}

function invoke(msg) {
    var randomnumber = Math.floor(Math.random() * 101)
    msg.put("stockquote.price", randomnumber);
    return true;
}
